class CountIntervals {
public:
  int tot = 0;
  set<pair<int, int>> all;

  CountIntervals() {
  }

  void add(int left, int right) {
    if (all.empty()) {
      all.insert({left, right});
      tot += (1 + right - left);
      return;
    }

    auto begin = all.begin();
    auto end = all.end();
    int first = left, last = right;
    vector<pair<int, int>> overlapping;

    auto init = all.lower_bound(make_pair(left, right));
    auto it = init;
    while (it != begin) {
      --it;
      if (it->second >= first) {
        first = min(first, it->first);
        last = max(last, it->second);
        overlapping.push_back(*it);
      } else {
        break;
      }
    }

    auto jt = init;
    while (jt != end) {
      if (jt->first <= last) {
        last = max(last, jt->second);
        overlapping.push_back(*jt);
      } else {
        break;
      }
      jt++;
    }

    for (auto pr : overlapping) {
      tot -= (1 + pr.second - pr.first);
      all.erase(pr);
    }
    tot += (1 + last - first);
    all.insert({first, last});
  }

  int count() {
    return tot;
  }
};


