class CountIntervals {
public:
    map<int,int> m;
    int ans;
    CountIntervals() {
        m.clear();
        m[0] = 0;
        ans=0;
    }

    void add(int left, int right) {
        auto it = m.upper_bound(left);
        int start, end;

        it--;
        if(left > it->second)
        {
            start = left;
            end   = left;
            ans += 1;
            it++;
        }
        else
        {
            start = it->first;
            end = it->first-1;
        }

        while(true)
        {
            if(it==m.end() || right<it->first)
            {
                ans += right-end;
                end = right;
                break;
            }
            int temp = it->first;
            ans += it->first-end-1;
            if(it->second >= right)
            {
                end = it->second;
                m.erase(m.find(temp));
                break;
            }
            end = it->second;
            it++;
            m.erase(m.find(temp));
        }
        m[start] = end;
    }
    int count() {
        return ans;
    }
};

