class CountIntervals {
    TreeMap<Integer,Integer> intervals;
    int count;

    public CountIntervals() {
        intervals= new TreeMap<>();
        count= 0;
    }

    public void add(int left, int right) {
        //1. merge with *one* interval for which start<left and end>=left
        Map.Entry<Integer,Integer> pre= intervals.floorEntry(left-1);
        if(pre!=null){
            int preLeft= pre.getKey(), preRight= pre.getValue();
            if(preRight>=left){
                int preCount= preRight-preLeft+1;
                count-= preCount;
                intervals.remove(preLeft);
                left= preLeft;
                right= Math.max(right, preRight);
            }
        }
        //2. merge with *all* intervals for which start>=left and start<=right
        while(true){
            Map.Entry<Integer,Integer> suf= intervals.ceilingEntry(left);
            if(suf!=null){
                int sufLeft= suf.getKey(), sufRight= suf.getValue();
                if(sufLeft<=right){
                    int sufCount= sufRight-sufLeft+1;
                    count-= sufCount;
                    intervals.remove(sufLeft);
                    right= Math.max(right, sufRight);
                }else break;
            }else break;            
        }
        intervals.put(left, right);
        count+= right-left+1;
    }

    public int count() {
        return count;
    }
}

