    map<int, int> intervals;
    int res = 0;
public:
    CountIntervals() {

    }

    void add(int left, int right) {
        auto it = intervals.lower_bound(left); // [right, left]
        pair<int, int> new_interval = {right, left};
		// erase all intervals intersecting with new_interval
        while (it != intervals.end() && it->second <= right) {
            new_interval.second = min(new_interval.second, it->second);
            new_interval.first = max(new_interval.first, it->first);
            res -= (it->first - it->second + 1);
            it = intervals.erase(it);
        }
        res += (new_interval.first - new_interval.second + 1);
        intervals.insert(new_interval);
    }

    int count() {
        return res;
    }
