    int cnt;
    CountIntervals() {
        m.clear();
        cnt=0;
    }

    void add(int left, int right) {
        auto ptr_l=m.upper_bound(left), ptr_r=m.upper_bound(right+1);
		//if the (--ptr_l)->right is >=left, that means (--ptr_l) will also be erased
		//检查一下前一个ptr是不是有可能要被erase掉
        if(ptr_l!=m.begin()){
            if((--ptr_l)->second<left)
                ptr_l++;
        }
		//subtract count in the range that will be erased
		//减去马上就要被erase的range
        for(auto ptr=ptr_l;ptr!=ptr_r;ptr++)
            cnt-=(ptr->second-ptr->first+1);

		//check the new range, 检查新的范围值
		//ll is the new left bound, and rr is the new right bound
        int ll=left;
        if(ptr_l!=m.end())
