            T[v] = op(T[v*2], T[v*2+1]);  // adjust
        }
    }
    void init(vector<X>& a, X base) {
        N = a.size();
        T.assign(4*N, base);
        BASE = base;
        build(1, 0, N-1, a);
    }
};

