class BookMyShow {
public:
    int n, m;
    int j = 0;  // MONOTONIC pointer, only advances forward. 
    vector<ll> A;
    SegTree<ll, fmax> mx;
    SegTree<ll, fsum> sum;

    BookMyShow(int n, int m) : n(n), m(m) {
        A.assign(n, m);
        sum.init(A, 0);
        mx.init(A, -inf);
    }

    vector<int> gather(int k, int maxRow) {
        int l = 0, r = maxRow+1;
        while (l < r) {
            int m = (l+r) >> 1;
            int mn = mx.query(0,m);
            if (mn >= k) r = m;
            else l = m+1;
        }
        if (l > maxRow) return {};
        r = m - A[l];
        A[l] -= k;
        sum.update(l, A[l]);
        mx.update(l, A[l]);
        return {l,r};
    }

    bool scatter(ll k, int maxRow) {
        ll s = sum.query(0,maxRow);
        if (s < k) return false;
        while (j < n && k) {
            int dx = min(k, A[j]);
            A[j] -= dx;
            sum.update(j, A[j]);
            mx.update(j, A[j]);
            k -= dx;
            if (!A[j]) ++j;
        }
        return true;
    }
};
