class BookMyShow {
    private int m;
    private int startRow;
    private int[] left;

    public BookMyShow(int n, int m) {
        this.m = m;
        left = new int[n];
        Arrays.fill(left, m);
    }

    public int[] gather(int k, int maxRow) {
        for (int i = startRow; i <= maxRow; i++) {
            if (left[i] >= k) {
                left[i] -= k;
                return new int[] {i, m - left[i] - k};
            }
        }
        return new int[] {};
    }

    public boolean scatter(int k, int maxRow) {
        int i = startRow;
        for ( ; i <= maxRow; i++) {
            if (k - left[i] < 0) {
                break;
            }
            k -= left[i];
        }
        if (i >= maxRow + 1) {
            if (k == 0) {
                startRow = i;
                return true;
            }
            return false;
        }
        startRow = i;
        left[i] -= k;
        return true;
    }
}
