template <class T>
class SegmentTree
{
private:
    long long n;
    vector<vector<T>> segtree;
public:
    void init(long long n)
    {
        this->n = n;
        segtree.resize(2, vector<T>(4 * n + 1));
    }
    T op(T x, T y, bool z)
    {
        return z ? (x + y) : max(x, y);
    }
    void updateVal(long long x, long long l, long long r, long long idx, T val)
    {
        if (l > idx or r < idx)
            return;
        if (l == r)
        {
            segtree[0][x] = val;
            segtree[1][x] = val;
            return;
        }
        long long mid = (l + r) / 2LL;
        updateVal(2 * x, l, mid, idx, val);
        updateVal(2 * x + 1, mid + 1, r, idx, val);
        segtree[0][x] = op(segtree[0][2 * x], segtree[0][2 * x + 1], 0);
        segtree[1][x] = op(segtree[1][2 * x], segtree[1][2 * x + 1], 1);
    }
    void update(long long idx, T val)
    {
        updateVal(1, 1, n, idx, val);
    }
    T cal(long long x, long long l, long long r, long long L, long long R)
    {
        if (L <= l and R >= r)
            return segtree[0][x];
        if (L > r or R < l)
            return 0;
        long long mid = (l + r) / 2LL;
        return op(cal(2 * x, l, mid, L, R), cal(2 * x + 1, mid + 1, r, L, R), 0);
    }
    T presum(long long x, long long l, long long r, long long L, long long R)
    {
        if (L <= l and R >= r)
            return segtree[1][x];
        if (L > r or R < l)
            return 0;
        long long mid = (l + r) / 2LL;
        return op(presum(2 * x, l, mid, L, R), presum(2 * x + 1, mid + 1, r, L, R), 1);
    }
    T query(long long l, long long r, long long z)
    {
        return l > r ? 0 : (z ? presum(1, 1, n, l, r) : cal(1, 1, n, l, r));
    }
};
class BookMyShow {
public:
    long long n, m, filled;
    SegmentTree<long long> stree;
    BookMyShow(long long n, long long m) {
        filled = 0;
        this->n = n;
        this->m = m;
        stree.init(n);
        for (long long i = 0; i < n; i++)
            stree.update(i + 1, m);
    }

    vector<long long> gather(long long k, long long maxRow) {
        long long lo = filled + 1, hi = maxRow + 1, ans = 0;
        while (lo <= hi)
        {
            long long mid = (lo + hi) >> 1LL;
            if (stree.query(filled + 1, mid, 0) >= k)
            {
                hi = mid - 1;
                ans = mid;
            }
            else
                lo = mid + 1;
        }
        if (!ans)
            return {};
        long long col = m - stree.query(ans, ans, 1);
        long long x = stree.query(ans, ans, 0);
        stree.update(ans, x - k);
        return {ans - 1, col};
    }

    bool scatter(long long k, long long maxRow) {
        if (stree.query(filled + 1, maxRow + 1, 1) < k)
            return false;
        long long lo = filled + 1, hi = maxRow + 1, ans;
        while (lo <= hi)
        {
            long long mid = (lo + hi) >> 1LL;
            if (stree.query(filled + 1, mid, 1) >= k)
            {
                hi = mid - 1;
                ans = mid;
            }
            else
                lo = mid + 1;
        }
        long long x = stree.query(filled + 1, ans, 1);
        filled = ans - 1;
        stree.update(ans, x - k);
        filled += (x == k);
        return true;
    }
};

