#define ll long long
class BookMyShow {
public:
    vector<ll> v;
    set<pair<ll,ll>> s;
    ll r,c,in=0;
    BookMyShow(int n, int m) {
        r=n;c=m;
        v.resize(n);
        for(int i=0;i<n;i++){
            v[i]=m;
            s.insert({m,i});
        }
    }
    vector<int> gather(int k, int maxRow) {
        auto it=s.lower_bound({k,-1});
        if(it==s.end())return {};
        int row=it->second,seats=it->first;
        if(row>maxRow)return {};
        s.erase(it);
        s.insert({seats-k,row});
        int x=v[row];
        v[row]-=k;
        return {row,(int)c-x};
    }

    bool scatter(int k, int maxRow) {
        ll sum=0,f=0;
        for(int i=in;i<=maxRow;i++){
            sum+=v[i];
            if(sum>=k){
                f=1;
                break;
            }
        }
        if(f==0)return 0;
        int i;
        for(i=in;i<=maxRow;i++){
            if(k<=v[i]){
                s.erase(s.find({v[i],i}));
                v[i]-=k;
                k=0;
                s.insert({v[i],i});
            }
            else{
                s.erase(s.find({v[i],i}));
                k-=v[i];
                v[i]=0;
            }
            if(k==0)break;
        }
        in=min(i,maxRow);
        return 1;
    }
};
