class BookMyShow {
public:

    int m, n;
    vector< long long > maxTree, sumTree;
    vector< int > filled;
    vector< int > res;

    void build(int it, int left, int right){

        if(left == right){
            maxTree[it] = m;
            sumTree[it] = m;
            return ;
        }
        int mid = (left + right) >> 1;

        build(2 * it, left, mid);
        build(2 * it + 1, mid + 1, right);
        maxTree[it] = max(maxTree[2 * it], maxTree[2 * it + 1]);
        sumTree[it] = sumTree[2 * it] + sumTree[2 * it + 1];
    }

    void queryMax(int it, int left, int right, int &k, int maxRow){
        if(left > maxRow || k == 0) return;
        if(maxTree[it] < k) return;
        if(left == right){
            res = {left, filled[left]};
            filled[left] += k;
            maxTree[it] -= k;
            sumTree[it] -= k;
            k = 0;
            return;
        }
        int mid = (left + right) >> 1;

        queryMax(2 * it, left, mid, k, maxRow);
        queryMax(2 * it + 1, mid + 1, right, k, maxRow);

        maxTree[it] = max(maxTree[2 * it], maxTree[2 * it + 1]);
        sumTree[it] = sumTree[2 * it] + sumTree[2 * it + 1];
    }

    void querySum(int it, int left, int right, int &k, int maxRow){
        if(left > maxRow || k == 0) return ;
        if(right <= maxRow){
            auto space = min(sumTree[it], (long long)k);
            k -= space;
            return ;
        }
        int mid = (left + right) >> 1;

        querySum(2 * it, left, mid, k, maxRow);
        querySum(2 * it + 1, mid + 1, right, k, maxRow);
    }

    void updateSum(int it, int left, int right, int &k, int maxRow){
        if(left > maxRow || k == 0) return ;
        if(left == right){
            auto space = min(sumTree[it], (long long)k);
            k -= space;
            filled[left] += space;
            maxTree[it] -= space;
            sumTree[it] -= space;
            return;
        }
        int mid = (left + right) >> 1;

        updateSum(2 * it, left, mid, k, maxRow);
        updateSum(2 * it + 1, mid + 1, right, k, maxRow);

        maxTree[it] = max(maxTree[2 * it], maxTree[2 * it + 1]);
        sumTree[it] = sumTree[2 * it] + sumTree[2 * it + 1];
    }



    BookMyShow(int n, int m) {
        this -> m = m;
        this -> n = n;
        maxTree.resize(4 * n + 1);
        sumTree.resize(4 * n + 1);
        filled.resize(n, 0);
        build(1, 0, n - 1);
    }

    vector<int> gather(int k, int maxRow) {
        res.clear();
        queryMax(1, 0, n - 1, k, maxRow);
        return res;
    }

    bool scatter(int k, int maxRow) {
        auto tmp = k;
        querySum(1, 0, n - 1, k, maxRow);
        if(k > 0) return 0;

        updateSum(1, 0, n - 1, tmp, maxRow);
        return 1;

    }
};

