Basic algorithm 

(I find it easier to implement compared to BIT and segment tree)

1. Divide N rows into sqrt(N) sized buckets.
2. Keep track of max and sum of all the buckets. 
3. For each operation, we need to operate on sqrt(N) buckets and sqrt(N) rows, (left over elements in one bucket)

```
class BookMyShow {
public:
    vector<long long> rows;
    long long bucketSize;
    vector<long long> bucketsSum;
    vector<long long> bucketsMax;
    long long totalBuckets;
    int M;
    int N;

    BookMyShow(int n, int m) {
        N = n;
        M = m;
        rows.resize(n, 0);
        bucketSize = sqrt(n);
        totalBuckets = n / bucketSize;
        if(n%bucketSize != 0){
            totalBuckets++;
        }
        bucketsSum.resize(totalBuckets, m*bucketSize);
        if(n%bucketSize != 0){
            bucketsSum[totalBuckets-1] = m * (n%bucketSize);
        }

        bucketsMax.resize(totalBuckets, m);
    }

    long long min(long long a, long long b){
        if(a >= b){
            return b;
        }else{
            return a;
        }
    }

    vector<int> gatherInBucket(int bucketNumber, int k, int maxRow){
        vector<int> ans;
        int start = bucketNumber * bucketSize;
        int end = min(N, start + bucketSize);
        for(int i=start;i<=maxRow;i++){
            if((M - rows[i]) >= k){
                ans.resize(2, 0);
                ans[0] = i;
                ans[1] = rows[i];
                rows[i] += k;
                bucketsSum[bucketNumber] = 0;
                bucketsMax[bucketNumber] = 0;
                for(int j=start;j<end;j++){
                    bucketsMax[bucketNumber] = max(bucketsMax[bucketNumber], (M-rows[j]));
                    bucketsSum[bucketNumber] +=  (M-rows[j]);
                }
                return ans;
            }
        }
        return ans;
    }

    vector<int> gather(int k, int maxRow) {
        vector<int> ans;
        if(k > M){
            return ans;
        }
        for(int i=0;i<totalBuckets;i++){
            if(bucketsMax[i] >= k){

                 ans = gatherInBucket(i, k, maxRow);
                return ans;
            }
        }
        return ans;
    }

    long long getSumInBucket(int bucketNumber, int maxRow){
        if(bucketsSum[bucketNumber] == 0){
            return 0;
        }
        int start = bucketNumber * bucketSize;
        int end = min(N, start + bucketSize);
        long long sumX = 0;
        for(int i=start;i<=maxRow;i++){
            sumX += (M - rows[i]);
        }
        return sumX;
    }

    bool scatter(int bucketNumber, int k, int maxRow){
        int start = bucketNumber * bucketSize;
        int end = min(N, start + bucketSize);
        for(int i=0;i<bucketNumber; i++){
            k -= bucketsSum[i];
            bucketsSum[i] = 0;
            bucketsMax[i] = 0;
        }
        for(int i=start;i<=maxRow && k > 0;i++){
            if(k >= (M - rows[i])){
                k -= (M - rows[i]);
                rows[i] = M;
            }else{
                rows[i] += k;
                k = 0;
            }
        }
        bucketsSum[bucketNumber] = 0;
        bucketsMax[bucketNumber] = 0;
        for(int j=start;j<end;j++){
            bucketsMax[bucketNumber] = max(bucketsMax[bucketNumber], (M-rows[j]));
            bucketsSum[bucketNumber] +=  (M-rows[j]);
        }
        return true;
    }

    bool scatter(int k, int maxRow) {
        long long sumX = 0;
        int bucketNumber;
        bool isPossible = false;
        for(int i=0;i<totalBuckets;i++){
            int end = min((i+1)*bucketSize, N);
            if(end > maxRow){
                bucketNumber = i;
                break;
            }
            sumX += bucketsSum[i];
            if(sumX >= k){
                bucketNumber = (i);
                isPossible = true;
                break;
            }
        }

        if(!isPossible && bucketNumber < totalBuckets){
            sumX += getSumInBucket(bucketNumber, maxRow);
        }
        if(sumX >= k){
            isPossible = true;
        }

        if(isPossible){
            scatter(bucketNumber, k, maxRow);
        }
        return isPossible;

    }
};

