class BookMyShow {
    rows: Record<number, number> = {};
    seatsPerRow;

    constructor(n: number, m: number) {
        // How many seats per row
        this.seatsPerRow = m;

        for (let i = 0; i < n; i++) {
            // Keep track of how many seats are available for a given row
            this.rows[i] = m;
        }
    }

    gather(k: number, maxRow: number): number[] {
        for (let i = 0; i <= maxRow; i++) {
            const seatsLeft = this.rows[i];

            // Find the first row with enough seats for the whole group
            if (k <= seatsLeft) {
                // When found, subtract the number needed for the group from the row
                this.rows[i] -= k;
                // Return the first seat used by this group
                // from the end of the row, go back how many were available initially
                // group seated :)
                return [i, this.seatsPerRow - seatsLeft];
            }
        }

        // Return an empty array if no row with enough seats found
        return [];
    }

    scatter(k: number, maxRow: number): boolean {
        // We decalre i out here to later find the last row used by the group
        let i;
        let seatsOnLastRow = 0;

        // Go through each row until all seats needed are gone
        // do NOT edit the seats count yet
        // we don't know yet if the group will end up using them
        for (i = 0; i <= maxRow; i++) {
            const seatsLeft = this.rows[i];

            if (k > seatsLeft) {
                // Return false if there are more people than seats on maxRow
                // we can't fulfill their request
                if (i === maxRow) return false;
                // Subtract the number of available seats in the row from the group
                k -= seatsLeft;
            } else {
                // Make a note of how many seats needed in the last row for the group
                seatsOnLastRow = k;
                break;
            }
        }

        // Fill all rows before the last
        for (let j = 0; j < i; j++) this.rows[j] = 0;
        // Subtract the needed number of seats on the last row
        this.rows[i] -= seatsOnLastRow;

        // Group seated :)
        return true;
    }
}
