class SegTree {
    vector<int>mx;
    vector<long long>sum;
    int cnt;

    public:
    SegTree(){}

    SegTree(int n) {
        mx.resize(4*n);
        sum.resize(4*n);
    }

    void update(int pos,int val,int st,int en,int node) {
        if(st > pos or en < pos) return;
        if(st == en) {
            mx[node] = val;
            sum[node] = val;
            return;
        }

        int mid = (st+en)>>1;

        update(pos,val,st,mid,2*node);
        update(pos,val,mid+1,en,2*node+1);
        mx[node] = max(mx[2*node],mx[2*node+1]);
        sum[node] = sum[2*node]+sum[2*node+1];
    }

    long long query(int l,int r,int st,int en,int node) {
        if(st > r or en < l) return 0;
        if(st >= l and en <= r) return sum[node];
        int mid = (st+en)>>1;
        return query(l,r,st,mid,2*node)+query(l,r,mid+1,en,2*node+1);
    }

    void nge(int val,int st,int en,int node) {
        if(st > cnt) return;
        if(mx[node] < val) {
            cnt += en-st+1;
            return;
        }

        if(st == en) return;

        int mid = (st+en)>>1;
        nge(val,st,mid,2*node);
        nge(val,mid+1,en,2*node+1);
    }

    int find_nge(int val,int n) {
        this->cnt = 0;
        nge(val,0,n-1,1);

        return cnt;
    }
};

class BookMyShow {
    int n,m;
    vector<int>col;
    SegTree tr;
    int start_idx;
public:
    BookMyShow(int n, int m) {
        start_idx = 0;
        tr = SegTree(n);
        this->n = n;
        this->m = m;
        col.assign(n,0);

        for(int i=0;i<n;i++) tr.update(i,m,0,n-1,1);
    }

    vector<int> gather(int k, int maxRow) {
        vector<int>ret;
        int r = tr.find_nge(k,n);

        if(r > maxRow) return ret;

        ret = {r,col[r]};

        col[r] += k;

        tr.update(r,m-col[r],0,n-1,1);

        return ret;
    }

    bool scatter(int k, int maxRow) {
        if(tr.query(0,maxRow,0,n-1,1) < k) return false;

        for(int i=start_idx;k > 0;i++) {
            if(m-col[i] <= k) {
                k -= m-col[i];
                col[i] = m;
                tr.update(i,0,0,n-1,1);
                start_idx = i;
            }
            else {
                col[i] += k;
                tr.update(i,m-col[i],0,n-1,1);
                k = 0;
            }
        }

        return true;
    }
};

