class BookMyShow {
public:
    using ll = long long;

    struct Node {
        ll sum;
        int mi, lazy;
    };

    int left(int i) {
        return 2*i + 1;
    }

    int right(int i) {
        return 2*i + 2;
    }

    vector<Node> tree;

    void pull(int si) {
        tree[si].sum = tree[left(si)].sum + tree[right(si)].sum;
        tree[si].mi = min(tree[left(si)].mi, tree[right(si)].mi);
    }

    void push(int ss, int se, int si) {
        if(tree[si].lazy == 0) return;

        tree[si].sum = tree[si].lazy * (ll)(se - ss + 1);
        tree[si].mi = tree[si].lazy;

        if(ss != se) {
            tree[left(si)].lazy = tree[si].lazy;
            tree[right(si)].lazy = tree[si].lazy;
        }

        tree[si].lazy = 0;  
    }

    pair<int,int> gather_update(int ss, int se, int si, int val, int r, int m) {
        push(ss, se, si);

        if(ss == se) {
            if(tree[si].sum + val <= m) {
                pair<int,int> ans = {ss, tree[si].sum};
                tree[si].sum += val;
                tree[si].mi += val;
                return ans;
            }

            return {-1, -1};
        }

        int mid = (ss + se) / 2;

        if(tree[left(si)].mi + val <= m) {
            pair<int,int> ans = gather_update(ss, mid, left(si), val, r, m);

            if(ans.first != -1) {
                pull(si);
            }

            return ans;
        }

        if(mid+1 <= r and tree[right(si)].mi + val <= m) {
            pair<int,int> ans = gather_update(mid+1, se, right(si), val, r, m);

            if(ans.first != -1) {
                pull(si);
            }

            return ans;
        }

        return {-1,-1};
    }

    void range_assign(int ss, int se, int si, int val, int r) {
        push(ss, se, si);

        if(ss > r) return;

        if(se <= r) {
            tree[si].lazy = val;
            push(ss, se, si);
            return;
        }

        int mid = (ss + se) / 2;

        range_assign(ss, mid, left(si), val, r);
        range_assign(mid+1, se, right(si), val, r);

        pull(si);
    }

    int scatter_update(int ss, int se, int si, int val, int r, int m) {
        push(ss, se, si);

        if(ss == se) {
            if(tree[si].sum + val <= m) {
                tree[si].sum += val;
                tree[si].mi += val;
                return ss;
            }

            return -1;
        }

        int mid = (ss + se) / 2;

        if(tree[left(si)].sum + val < (ll)(mid-ss+1) * m) {
            int ind = scatter_update(ss, mid, left(si), val, r, m);

            if(ind != -1) {
                pull(si);
            }

            return ind;
        }

        if(mid+1 <= r) {
            int ind = scatter_update(mid+1, se, right(si), val - ((ll)(mid-ss+1)*m - tree[left(si)].sum), r, m);

            if(ind != -1) {
