    vector<int> gather(int k, int maxRow) {
        pair<int,int> ans = gather_update(0, n-1, 0, k, maxRow, m);

        if(ans.first != -1) return vector<int>{ans.first, ans.second};
        return {};
    }

    bool scatter(int k, int maxRow) {
        int ind = scatter_update(0, n-1, 0, k, maxRow, m);

        if(ind == -1) return false;

        range_assign(0, n-1, 0, m, ind-1);

