            t[i] = max(t[i * 2], t[i * 2 + 1]);
        for (int i, j = n - 1; j >= 0; --j)
            b[j] -= (i = j & (j + 1)) > 0 ? b[i - 1] : 0;
    }

    ~BookMyShow() { delete[] t; delete[] b; }

    vector<int> gather(int k, int mr) {
        int r = get_r(k), c;
        if (r > mr) return {};
        c = m - t[r + tz];
        update(r, k);
        return {r, c};
    }

    bool scatter(int k, int mr) {
        if (pref_sum(mr) < k) return false;
        int i = get_r(1), x, d;
        while (k > 0) {
            if ((x = t[i + tz]) > 0) {
                k -= d = min(x, k);
                update(i, d);
            }
            i++;
        }
        return true;
    }

};
