class BookMyShow {
  rowsOccupiedCount: number[]=[];
  maxOccupied: number;
  constructor(n: number, m: number) {
      this.fillFullRows(n, m);
      this.maxOccupied = m;
  }

  fillFullRows(n: number, m: number) {
    this.rowsOccupiedCount = [];
    for (let i = 0; i < n; i++) {
        this.rowsOccupiedCount.push(0);
    }
  }

  gather(k: number, maxRow: number): number[] {
    if (k > this.maxOccupied) {
        return [];
    }
    for (let i = 0; i < this.rowsOccupiedCount.length; i++) {
        const occupied = this.rowsOccupiedCount[i];
        if (i > maxRow) {
            break;
        }
        if (occupied + k > this.maxOccupied) {
            continue;
        }

        this.rowsOccupiedCount[i] += k;

        return [i, occupied];
    }
    return [];
  }

  scatter(k: number, maxRow: number): boolean {
    const occupiedSum = this.rowsOccupiedCount.reduce((acc, row, i) => {
        if (i <= maxRow) {
            return acc + row;
        }
        return acc;
    }, 0);
    const allFree = this.maxOccupied * (maxRow + 1) - occupiedSum;

    if (allFree < k) {
        return false;
    }

    for (let i = 0; i < this.rowsOccupiedCount.length; i++) {
        const free = this.maxOccupied - this.rowsOccupiedCount[i]; 
        if (k > free) {
            k -= this.maxOccupied - this.rowsOccupiedCount[i];
            this.rowsOccupiedCount[i] = this.maxOccupied;
            continue;
        }

        this.rowsOccupiedCount[i] += k;
        return true;        
    }
    return true;
  }
}

