public class BookMyShow
    {
        /// <summary>
        /// A "gap" represents a sequence of empty seats over an arbitrary number of rows.
        /// A gap can start on any seat, but will always end on the final seat of a row.
        /// Where a gap is spread over multiple rows, all rows other than the starting row will be empty
        /// </summary>
        public class Gap
        {
            /// <summary>
            /// The number of empty seats in this gap
            /// </summary>
            public long GapSize { get; private set; }

            /// <summary>
            /// The index of seat on which the gap starts.
            /// This will be equal to the row index * seatsPerRow + the seat number in that row
            /// </summary>
            public long StartingSeatNumber { get; private set; }

            /// <summary>
            /// The index of the row that the StartingSeatNumber is situated on
            /// </summary>
            public int StartRowIndex { get; private set; }

            /// <summary>
            /// The index of the last row in the gap
            /// </summary>
            public int EndRowIndex { get; private set; }

            public Gap(long gapSize, long startingSeatNumber, int startRowIndex, int endRowIndex)
            {
                GapSize = gapSize;
                StartingSeatNumber = startingSeatNumber;
                StartRowIndex = startRowIndex;
                EndRowIndex = endRowIndex;
            }

            /// <summary>
            /// Fills in seats in the gap starting from the StartingSeatNumber. This may fill more than one row
            /// </summary>
            /// <param name="spacesToFill">The number of spaces to fill</param>
            /// <param name="seatsPerRow">Number of seats per row</param>
            public void FillFromStart(long spacesToFill, int seatsPerRow)
            {
                GapSize -= spacesToFill;
                StartingSeatNumber += spacesToFill;
                StartRowIndex = GetRowIndexFromSeat(StartingSeatNumber, seatsPerRow);
            }

            /// <summary>
            /// Completely fill in the last row in this gap
            /// </summary>
            /// <param name="seatsPerRow">Number of seats per row</param>
            public void FillLastRow(int seatsPerRow)
            {
                GapSize -= seatsPerRow;
                EndRowIndex--;
            }

            /// <summary>
            /// Split this gap into 2, where this gap will now terminate at the end of its current row, and the new gap will start on the next row, and go up to this gap's former end row index
            /// </summary>
            /// <param name="seatsPerRow">Number of seats per row</param>
            /// <param name="spacesToFill">Number of spaces to fill in the new gap</param>
            /// <param name="newGap">The new gap object</param>
            public void Split(int seatsPerRow, int spacesToFill, out Gap newGap)
            {
                long seatsLeftInStartRow = GetSeatsLeftInStartRow(seatsPerRow);
                long nextRowNumber = StartRowIndex + 1;
                long newGapSize = GapSize - seatsLeftInStartRow - spacesToFill;
                long newGapStartingSeatNumber = (long)nextRowNumber * seatsPerRow + spacesToFill;
                newGap = new Gap(newGapSize, newGapStartingSeatNumber, GetRowIndexFromSeat(newGapStartingSeatNumber, seatsPerRow), EndRowIndex);
                GapSize = seatsLeftInStartRow;
                EndRowIndex = GetRowIndexFromSeat(StartingSeatNumber + GapSize - 1, seatsPerRow);
            }

            private long GetSeatsLeftInStartRow(int seatsPerRow)
            {
                return seatsPerRow - (StartingSeatNumber % seatsPerRow);
            }

            /// <summary>
            /// Calculates whether there's enough space in the starting row in this gap to fill the first row during a gather
            /// </summary>
            /// <param name="seatsPerRow">Number of seats per row</param>
            /// <param name="peopleCount">Number of spaces required</param>
            /// <returns>true if there is enough space</returns>
            public bool CanGatherInStartRow(int seatsPerRow, long peopleCount)
            {
                return GetSeatsLeftInStartRow(seatsPerRow) >= peopleCount;
            }
        }

        private static readonly int[] invalidGatherResult = new int[0];

        private readonly LinkedList<Gap> gaps;
        private readonly int rowCount;
        private readonly int seatsPerRow;
        private long totalSeatsAvailable;
        private (int, LinkedListNode<Gap>?) lastGatherResult;

        public BookMyShow(int rowCount, int seatsPerRow)
        {
            this.rowCount = rowCount;
            this.seatsPerRow = seatsPerRow;
            totalSeatsAvailable = ((long)rowCount) * seatsPerRow;
            gaps = new LinkedList<Gap>();
            gaps.AddLast(new Gap(totalSeatsAvailable, 0, 0, rowCount - 1));
            lastGatherResult = (-1, default);
        }

        public int[] Gather(int peopleCount, int maxRow)
        {
            if (peopleCount > seatsPerRow)
            {
                return invalidGatherResult;
            }
            if (!EnoughSeatsAvailable(peopleCount, maxRow))
            {
                return invalidGatherResult;
            }
            LinkedListNode<Gap>? currentGap = GetStartingGatherNode(peopleCount);
            while (currentGap != null)
            {
                Gap gap = currentGap.Value;
                if (gap.GapSize >= peopleCount)
                {
                    int startRowNumber = gap.StartRowIndex;

                    if (startRowNumber > maxRow)
                    {
                        return invalidGatherResult;
                    }
                    if (gap.CanGatherInStartRow(seatsPerRow, peopleCount))
                    {
                        int[] result = new[] { startRowNumber, (int)(gap.StartingSeatNumber % seatsPerRow) };
                        gap.FillFromStart(peopleCount, seatsPerRow);
                        totalSeatsAvailable -= peopleCount;
                        lastGatherResult = (peopleCount, currentGap);
                        return result;
                    }
                    // if there's a next row, it's guaranteed to be empty
                    if (startRowNumber != gap.EndRowIndex)
                    {
                        int nextRowNumber = startRowNumber + 1;
                        if (nextRowNumber > maxRow)
                        {
                            return invalidGatherResult;
                        }
                        if (peopleCount == seatsPerRow
                            && nextRowNumber == gap.EndRowIndex)
                        {
                            gap.FillLastRow(seatsPerRow);
                        }
                        else
                        {
                            gap.Split(seatsPerRow, peopleCount, out Gap newGap);
                            currentGap = gaps.AddAfter(currentGap, newGap);
                        }
                        totalSeatsAvailable -= peopleCount;
                        lastGatherResult = (peopleCount, currentGap);
                        return new[] { nextRowNumber, 0 };
                    }
                }
                currentGap = currentGap.Next;
            }
            return invalidGatherResult;
        }

        public bool Scatter(int peopleCount, int maxRow)
        {
            if (!EnoughSeatsAvailable(peopleCount, maxRow))
            {
                return false;
            }
            long peopleCountL = peopleCount;
            lastGatherResult = (-1, default);
            LinkedListNode<Gap>? currentGap = gaps.First;
            while (currentGap != null
                && peopleCountL > 0)
            {
                Gap gap = currentGap.Value;
                long seatsInGapToRemove = Math.Min(peopleCountL, gap.GapSize);
                gap.FillFromStart(seatsInGapToRemove, seatsPerRow);
                peopleCountL -= seatsInGapToRemove;
                if (gap.GapSize == 0)
                {
                    // Due to the way the gaps are populated, if a gap has been filled it will always be the first gap
                    gaps.RemoveFirst();
                    currentGap = gaps.First;
                }
                else
                {
                    currentGap = currentGap.Next;
                }
            }
            totalSeatsAvailable -= peopleCount;
            return true;
        }

        private bool EnoughSeatsAvailable(int requiredSeats, long maxRow)
        {
            if (totalSeatsAvailable < requiredSeats)
            {
                return false;
            }
            if (maxRow >= rowCount - 1)
            {
                return true;
            }
            if (gaps.Last.Value.StartRowIndex < maxRow)
            {
                long invalidSeats = (rowCount - maxRow - 1) * seatsPerRow;
                return totalSeatsAvailable - invalidSeats >= requiredSeats;
            }
            long availableSeats = 0;
            foreach (Gap gap in gaps)
            {
                if (gap.StartRowIndex > maxRow)
                {
                    // all subsequent gaps will be the same, so abort the loop
                    break;
                }
                availableSeats += seatsPerRow - (gap.StartingSeatNumber % seatsPerRow);
                availableSeats += seatsPerRow * (Math.Min(maxRow, gap.EndRowIndex) - (gap.StartRowIndex));
                if (availableSeats >= requiredSeats)
                {
                    return true;
                }
            }
            return availableSeats >= requiredSeats;
        }

        private LinkedListNode<Gap>? GetStartingGatherNode(int peopleCount)
        {
            if (lastGatherResult.Item1 != -1
                && lastGatherResult.Item1 <= peopleCount)
            {
                return lastGatherResult.Item2;
            }
            return gaps.First;
        }

        private static int GetRowIndexFromSeat(long seatNumber, long seatsPerRow)
        {
            return (int)(seatNumber / seatsPerRow);
        }
    }
