public class BookMyShow {
    (long max, long sum)[] st;
    int m,n;
    int N;
    int size;
    public BookMyShow(int n, int m) {
        this.m=m;
        this.n=n;
        N=1;
        while(N<n)
            N=N<<1;
        size=2*N-1;
        st=new (long max, long sum)[size];

        InitializeTree();



    }

    public int[] Gather(int k, int maxRow) {
        int[] result= new int[2];
        int[] r;
        if(!AllocateGather(0,k,maxRow, 0, N-1,  result))
            return new int[]{};
        return result;
    }


    public bool Scatter(int k, int maxRow) {
        int a=k;
        CheckAvailability(0, ref a,  maxRow, 0,N-1);
        if(a>0)
            return false;
        long allocated=AllocateScatter( 0, k,  maxRow, 0,N-1);

        return true;

    }


    public bool AllocateGather(int idx, int k, int maxRow, int low, int high, int[] result)
    {
        if(low>maxRow || st[idx].max<k)
            return false;

        if(idx>=N-1){ //IsLeaf
            result[0]=idx-(N-1);
            result[1]=(int)((long)m -st[idx].max);
            st[idx].max -=k;
            st[idx].sum-=k;

            return true;
        }

        if(AllocateGather(2*idx+1,k,maxRow, low, (low+high)/2,result))
        {
            st[idx].max=Math.Max(st[2*idx+1].max,st[2*idx+2].max);
            st[idx].sum=st[2*idx+1].sum+st[2*idx+2].sum;
        }
        else if(AllocateGather(2*idx+2,k,maxRow, (low+high)/2+1, high,result)){
            st[idx].max=Math.Max(st[2*idx+1].max,st[2*idx+2].max);
            st[idx].sum=st[2*idx+1].sum+st[2*idx+2].sum;

        }
        else 
            return false;

        return true;

    }

    public long AllocateScatter(int idx, int k, int maxRow, int low, int high){
        if(low>maxRow || st[idx].sum==0 || k<=0)
            return 0;
        long allocated=0;

        if(idx>=N-1){ //IsLeaf
            allocated= Math.Min(k,st[idx].sum); 
            st[idx].max -=allocated;
            st[idx].sum-=allocated;

            return allocated;
        }

        allocated+=AllocateScatter(2*idx+1,k-(int)allocated,maxRow, low, (low+high)/2);
        allocated+=AllocateScatter(2*idx+2,k-(int)allocated,maxRow, (low+high)/2+1, high);
        st[idx].max=Math.Max(st[2*idx+1].max,st[2*idx+2].max);
        st[idx].sum=st[2*idx+1].sum+st[2*idx+2].sum;

        return allocated;
    }


    public void CheckAvailability(int idx, ref int k, int maxRow, int low, int high)
    {

        if(low>maxRow )
            return ;

        if(high <= maxRow ){
            k=k-(int)Math.Min(k,st[idx].sum);
            return;
        }
        if(idx>=N-1){ //IsLeaf
            k=k-(int)Math.Min(k,st[idx].sum);
            return;
        }

        CheckAvailability(2*idx+1,ref k,maxRow, low, (low+high)/2);
        CheckAvailability(2*idx+2,ref k,maxRow, (low+high)/2+1, high);


    }

    public void InitializeTree()
    {
        for(int b=N-1;b<Math.Min(2*N-1,n+N-1);b++)
            st[b]=(m,m);
        int l=N-1;
        while(l>0){
            l=(l-1)/2;
            for(int b=l;b<2*l+1;b++)
            {
                st[b]=(m, st[2*b+1].sum+st[2*b+2].sum);
            }
        }

    }
}

