from bisect import bisect_left
from collections import deque


class BookMyShow:
    def __init__(self, n, m):
        self.n, self.m = n, m
        self.free_row = 0
        self.partial_rows = deque()
        self.maximums = deque()

    def gather(self, k, maxRow):
        if k > self.m:
            return []
        lowest = self.use_lowest_partial_row(k, maxRow)
        if not lowest:
            lowest = self.use_free_row(k, maxRow)
        return lowest

    def scatter(self, k, maxRow):
        found = 0
        found_last = found_last_i = -1
        for i, (row, seats_remaining) in enumerate(self.partial_rows):
            if row > maxRow:
                break
            found += seats_remaining
            found_last = seats_remaining
            found_last_i = i
            if found >= k:
                break
        k_remaining = k - min(found, k)
        if not self.check_free_rows(k_remaining, maxRow):
            return False
        self.use_free_rows(k_remaining)
        if found_last != -1:
            used = k - k_remaining
            before_last = found - found_last
            used_last = used - before_last
            last_left = found_last - used_last
            if last_left:
                for _ in range(found_last_i):
                    self.pop()
                self.partial_rows[0][1] -= used_last
            else:
                for _ in range(found_last_i+1):
                    self.pop()
        return True

    def push(self, row, seats_remaining):
        if seats_remaining:
            self.partial_rows.append([row, seats_remaining])
            self.maximums.append(
                max(seats_remaining, self.maximums[-1] if self.maximums else 0))

    def pop(self):
        self.maximums.popleft()
        return self.partial_rows.popleft()

    def use_free_row(self, k, maxRow):
        if self.free_row > maxRow:
            return []
        self.push(self.free_row, self.m-k)
        ans = [self.free_row, 0]
        self.free_row += 1
        return ans

    def check_free_rows(self, k, maxRow):
        if k == 0:
            return True
        free_rows_between = maxRow - self.free_row + 1
        free_seats_between = free_rows_between * self.m
        return free_seats_between >= k

    def use_free_rows(self, k):
        entire_rows_used, partial_row_used = divmod(k, self.m)
        self.free_row += entire_rows_used + 1
        self.push(self.free_row-1, self.m - partial_row_used)

    def use_lowest_partial_row(self, k, maxRow):
        if not self.partial_rows or k <= 0:
            return []
        for i in range(max(bisect_left(self.maximums, k)-1, 0), len(self.partial_rows)):
            row, seats_remaining = self.partial_rows[i]
            if row > maxRow:
                break
            if seats_remaining > k:
                self.partial_rows[i][1] -= k
                return [row, self.m-seats_remaining]
            elif seats_remaining == k:
                del self.partial_rows[i]
                return [row, self.m-seats_remaining]
        return []
