To calculate for gather function we need to find the first index where we have space for k people. We can keep this count blockwise first, and then search within each block. Similarly for scatter function, we need to find if the sum of spaces until maxRow is sufficient for k people. Sample implementation below:

```
class BookMyShow {
private:
    static const int lim = 5e4+5;
    static const int blok = 225;
    int n, m;
    int seats[lim], fastmax[blok], hasanyseat; 
    long long fastsum[blok];
public:
    BookMyShow(int n, int m) {
        this->n = n;
        this->m = m;
        fill(seats, seats+lim, m);
        fill(fastmax, fastmax+blok, m);
        for(int i=0;i<n;i++) {
            fastsum[i/blok] += m;
        }
        hasanyseat = 0;
    }

    void recompute(int blkid) {
        fastmax[blkid] = 0;
        fastsum[blkid] = 0;
        for(int i=blkid*blok;i<min((blkid+1)*blok, n);i++) {
            fastmax[blkid] = max(fastmax[blkid], seats[i]);
            fastsum[blkid] += seats[i];
        }
    }

    vector<int> gather(int k, int maxRow) {
        int flim = maxRow/blok;
        int row = -1;
        for(int i=0;i<=flim;i++) {
            if(fastmax[i]>=k) {
                for(int j=i*blok;j<min((i+1)*blok, maxRow+1);j++) {
                    if(seats[j]>=k) {
                        row = j;
                        seats[j] -= k;
                        recompute(i);
                        break;
                    }
                }
                if(row!=-1) {
                    break;
                }
            }
        }
        if(row==-1) {
            return vector<int>();
        }
        return vector<int>{row, m-seats[row]-k};
    }

    bool scatter(int k, int maxRow) {
        long long avail = 0;
        for(int i=0;i<maxRow/blok;i++) {
            avail += fastsum[i];
        }
        for(int i=maxRow/blok*blok;i<=maxRow;i++) {
            avail += seats[i];
        }
        if(avail<k) {
            return false;
        }

        unordered_set<int> bloks_impacted;
        while(k>0) {
            int alloc = min(k, seats[hasanyseat]);
            seats[hasanyseat] -= alloc;
            k -= alloc;
            bloks_impacted.insert(hasanyseat/blok);
            if(seats[hasanyseat]==0) {
                hasanyseat++;
            }
        }
        for(int blok:bloks_impacted) {
            recompute(blok);
        }
        return true;
    }
};
