            self.update_sum1(1,0,self.n-1,a,a,b)
            return [a,self.m-b-k]
        return []

    def scatter(self, k: int, maxRow: int) -> bool:
        a,b=self.query_sum(1,0,self.n-1,k)
        if a<=maxRow and b>=0:
            self.update_sum1(1,0,self.n-1,a,a,b)
            self.update_sum1(1,0,self.n-1,self.cur,a-1,0)
            if a-1>self.cur:
                self.cur=a-1
            return True
        return False

