Similar to the current top post

Complexity in scatter is amortized in ```N*LogN``` with each call costing ```LogN```
Complexity in gather for each call is bin search * segment_tree search = ```log^2 N```

```cpp
# define ll long long

struct segment_tree {
    struct node{ll max_avail; ll total_avail; }; 
    ll levels = 1; 
    ll alloc = 1;
    vector<node> st; 
    segment_tree(ll n) {
        while (alloc < n) {
            levels ++;
            alloc <<= 1;
        };
        st.resize(alloc * 2 - 1);
    }

    ll GO_UP(ll x) { return (x - 1) / 2; }
    ll GO_LEFT(ll x) { return (x * 2) + 1; }
    ll GO_RIGHT(ll x) { return (x + 1) * 2; }

    bool segment_A_consumes_B(ll Al, ll Ar, ll Bl, ll Br) { return (Al <= Bl && Ar >= Br);}
    bool segment_A_nooverlap_B(ll Al, ll Ar, ll Bl, ll Br) {return (Al > Br || Ar < Bl || Bl > Ar || Br < Al);}

    node reduce(node a, node b) {
        return {max(a.max_avail, b.max_avail), a.total_avail + b.total_avail};
    }   

    void update(ll index, node val) {
        ll ref = (alloc - 1) + (index - 1); 
        st[ref] = val;
        while (ref != 0) {
            ll parent = GO_UP(ref);
            st[parent] = reduce(st[GO_LEFT(parent)], st[GO_RIGHT(parent)]);
            ref = parent;
        }
    }

    node query(ll lft, ll rht) {return query(lft, rht, 1, alloc, 0);}
    node query(ll lft, ll rht, ll plft, ll prht, ll n) {
        if (segment_A_consumes_B(lft, rht, plft, prht)) {
            return st[n];
        }
        else if (segment_A_nooverlap_B(lft, rht, plft, prht)) {
            return {0, 0};
        }
        else {
            node lsol = query(lft, rht, plft, ((prht - plft) / 2) + plft, GO_LEFT(n));
            node rsol = query(lft, rht, ((prht - plft) / 2) + plft + 1, prht, GO_RIGHT(n));
            return reduce(lsol, rsol);
        }
    }
};


class BookMyShow {
public:
    ll last_row = 1;
    ll M; ll N;
    segment_tree t = segment_tree(1);
    BookMyShow(ll n, ll m) {
        M = m; N = n;
        t = segment_tree(n + 1);
        for (ll i = 1; i <= n + 1; i ++) {
            t.update(i, {m, m});
        }
    }

    vector<int> gather(ll k, ll maxRow) {
        ll low = 1; 
        ll high = maxRow + 1;
        while (low < high) {
            ll mid = (low + high) / 2;
            if (t.query(1, mid).max_avail >= k) {
                high = mid;
            }
            else {
                low = mid + 1;
            }
        }
        if (t.query(1, low).max_avail >= k) {
            ll row = low; 
            ll totalAvail = t.query(low, low).total_avail;
            t.update(low, {totalAvail - k, totalAvail - k});
            return {(int)low - 1, (int)(M - totalAvail)};
        }
        else {
            return {};
        }
    }

    bool scatter(ll k, ll maxRow) {
        if (t.query(1, maxRow + 1).total_avail >= k) {
            while (k != 0) {
                ll avail = t.query(last_row, last_row).total_avail;
                if (avail > 0) {
                    ll new_allocation = min(k, avail);
                    k -= new_allocation; 
                    t.update(last_row, {avail - new_allocation, avail - new_allocation});
                }
                else {
                    last_row ++;
                }
            }
            return true;
        }
        return false;
    }
};
