Initialization is O(n) Time
group is O(lg(n)) Time
scater is O(k*lg(n)) Time
O(n) Space
```
class BookMyShow:

    def __init__(self, n: int, m: int):
        self.fwtree = FwTree(n,m)
        self.sgt = SegTree(n,m)
        self.m = m

    def gather(self, k: int, maxRow: int) -> List[int]:
        ff = self.sgt.firstFit(k)
        if ff and ff[0] <= maxRow:
            (row, available) = ff
            self.fwtree.update(row, available-k)
            self.sgt.update(row, available-k)
            return [row, self.m-available]
        else:
            return []

    def scatter(self, k: int, maxRow: int) -> bool:
        if self.fwtree.getSum(maxRow) >= k:
            while k > 0:
                row, available = self.sgt.firstFit(1)
                fit = min(available, k)
                self.fwtree.update(row,available-fit)
                self.sgt.update(row,available-fit)
                k -= fit

            return True
        else:
            return False

class FwTree:
    def __init__(self,n,m):
        self.t = [0]*(n+1)
        for i in range(1,len(self.t)):
            self.t[i]+=m
            if (j:=FwTree.__getNext(i)) < len(self.t):
                self.t[j] += self.t[i]

    def update(self,i,val):
        i+=1
        delta = val - self.t[i]
        while i < len(self.t):
            self.t[i] += delta
            i = FwTree.__getNext(i)

    @classmethod
    def __getNext(cls,b):
        return (b & -b) + b

    @classmethod
    def __getPrev(cls,b):
        return b - (b & -b)

    def getSum(self,i):
        i+=1
        res = 0
        while i > 0:
            res += self.t[i]
            i = FwTree.__getPrev(i)
        return res

class SegTree:
    def __init__(self, n, m):
        self.m = m
        self.np2 = 1
        while self.np2 < n:
            self.np2 <<= 1
        self.np2 -= 1
        self.t = [m]*(self.np2+n)

    def update(self,i,val):
        i += self.np2
        self.t[i] = val
        while (i := (i-1)//2) >= 0:
            l = self.t[2*i+1]
            j = 2*i+2
            r = self.t[j] if j < len(self.t) else 0
            self.t[i] = max(l,r)

    def firstFit(self,k):
        if self.t[0] >= k:
            i = 0
            while True:
                if i >= self.np2:
                    return [i-self.np2, self.t[i]]
                else:
                    n = 2*i+1
                    i = n if self.t[n] >= k else n+1
        else:
            return None
