class BiaSum:
    def __init__(self, n, m):
        self.n = n
        self.arr = [0]*(n+1)
        for i in range(n):
            self.update(i+1, m)
    def update(self, idx, val):
        while idx<=self.n:
            self.arr[idx]+=val
            idx+=-idx&idx
    def query(self, idx):
        res = 0
        while idx>0:
            res+=self.arr[idx]
            idx-=-idx&idx
        return res
class BiaMax:
    def __init__(self, n, m):
        self.n = n
        self.arr, self.orig = [m]*(n+1), [m]*(n+1)
        self.arr[0] = self.orig[0] = 0
    def update(self, idx, val):
        self.orig[idx] += val
        while idx<=self.n:
            self.arr[idx] = self.orig[idx]
            lowbit, start = -idx&idx, 1
            while start<lowbit:
                self.arr[idx] = max(self.arr[idx], self.arr[idx-start])
                start<<=1
            idx+=lowbit
    def query(self, idx):
        res = -inf
        while idx>0:
            res = max(res, self.arr[idx])
            idx-=-idx&idx
        return res

class BookMyShow:

    def __init__(self, n: int, m: int):
        self.biasum, self.biamax = BiaSum(n, m), BiaMax(n, m)
        self.m, self.cur = m, 0

    def gather(self, k: int, maxRow: int) -> List[int]:
        if self.biamax.query(maxRow+1)<k: 
            return []
        l, r = 1, maxRow+1
        while l<r:
            mid = l+(r-l)//2
            if self.biamax.query(mid)>=k:
                r = mid
            else:
                l = mid+1
        res = [l-1, self.m-self.biamax.orig[l]]
        self.biamax.update(l, -k)
        self.biasum.update(l, -k)
        return res

    def scatter(self, k: int, maxRow: int) -> bool:
        if self.biasum.query(maxRow+1)<k: return False
        l, r = 0, maxRow+1
        while l<r:
            mid = l+(r-l)//2
            if self.biasum.query(mid)>=k:
                r = mid
            else:
                l = mid+1
        cnt = 0
        while self.cur<l-1:
            cur_avail = self.biamax.orig[self.cur+1]
            cnt += cur_avail
            self.biamax.update(self.cur+1, -cur_avail)
            self.biasum.update(self.cur+1, -cur_avail)
            self.cur += 1
        self.biamax.update(l, cnt-k)
        self.biasum.update(l, cnt-k)
        return True

