            : segment_tree_((1 << static_cast<int>(ceil(log2(rows)) + 1)) - 1, make_pair(0LL, 0)), rows_(rows), cols_(cols) {
            build(0, 0, rows);
        }

        pair<int, int> query_max(const int k, const int max_row) {
            return query_max_impl(0, 0, rows_, k, max_row);
        }

        void update_max(const int row, const int delta) {
            update_max_impl(0, 0, rows_, row, delta);
        }

        long long query_sum(const int max_row) {
            return query_sum_impl(0, 0, rows_, max_row);
        }

        void update_sum(const int max_row, int k) {
            update_sum_impl(0, 0, rows_, max_row, k);
        }

    private:
        void build(const int node, const int start, const int end) {
            if (end - start == 1) {
                segment_tree_[node] = make_pair(static_cast<long long>(cols_), cols_);
                return;
            }

            const int left_node = 2 * node + 1;
            const int right_node = 2 * node + 2;
            const int mid = start + (end - start) / 2;
            build(left_node, start, mid);
            build(right_node, mid, end);
            segment_tree_[node].first = segment_tree_[left_node].first + segment_tree_[right_node].first;
            segment_tree_[node].second = max(segment_tree_[left_node].second, segment_tree_[right_node].second);
        }

        pair<int, int> query_max_impl(const int node, const int start, const int end, const int k, const int max_row) {
            if (start > max_row) {
                return make_pair(-1, -1);
            }

            if (segment_tree_[node].second < k) {
                return make_pair(-1, -1);
            }

            if (end - start == 1) {
                return make_pair(start, cols_ - segment_tree_[node].second);
            }

            const int left_node = 2 * node + 1;
            const int right_node = 2 * node + 2;
            const int mid = start + (end - start) / 2;
            const pair<int, int> result_left = query_max_impl(left_node, start, mid, k, max_row);
            if (result_left.first != -1) {
                return result_left;
            }
            return query_max_impl(right_node, mid, end, k, max_row);
        }

        void update_max_impl(const int node, const int start, const int end, const int row, const int delta) {
            if (row < start || row >= end) {
                return;
            }

            if (end - start == 1) {
                segment_tree_[node].first -= delta;
                segment_tree_[node].second -= delta;
                return;
            }

            const int left_node = 2 * node + 1;
            const int right_node = 2 * node + 2;
            const int mid = start + (end - start) / 2;
            update_max_impl(left_node, start, mid, row, delta);
            update_max_impl(right_node, mid, end, row, delta);
            segment_tree_[node].first = segment_tree_[left_node].first + segment_tree_[right_node].first;
            segment_tree_[node].second = max(segment_tree_[left_node].second, segment_tree_[right_node].second);
        }

        long long query_sum_impl(const int node, const int start, const int end, const int max_row) {
            if (start > max_row) {
                return 0LL;
            }

            if (end <= max_row + 1) {
                return segment_tree_[node].first;
            }

            const int left_node = 2 * node + 1;
            const int right_node = 2 * node + 2;
            const int mid = start + (end - start) / 2;
            return query_sum_impl(left_node, start, mid, max_row) + query_sum_impl(right_node, mid, end, max_row);
        }

        void update_sum_impl(const int node, const int start, const int end, const int max_row, int &k) {
            if (start > max_row) {
                return;
            }

            if (k <= 0) {
                return;
            }

            if (end - start == 1) {
                if (segment_tree_[node].first >= k) {
                    segment_tree_[node].first -= k;
                    segment_tree_[node].second -= k;
                    k = 0;
                } else {
                    k -= segment_tree_[node].first;
                    segment_tree_[node].first = 0;
                    segment_tree_[node].second = 0;
                }
                return;
            }

            const int left_node = 2 * node + 1;
            const int right_node = 2 * node + 2;
            const int mid = start + (end - start) / 2;
            update_sum_impl(left_node, start, mid, max_row, k);
            update_sum_impl(right_node, mid, end, max_row, k);
            segment_tree_[node].first = segment_tree_[left_node].first + segment_tree_[right_node].first;
            segment_tree_[node].second = max(segment_tree_[left_node].second, segment_tree_[right_node].second);
        }

        vector<pair<long long, int>> segment_tree_;   // {{sum of the available cells, max available cells}, ...}
        const int rows_;
        const int cols_;
    };

public:
    BookMyShow(const int n, const int m) : segment_tree_(n, m) {
    }

    vector<int> gather(int k, int maxRow) {
        const pair<int, int> ret = segment_tree_.query_max(k, maxRow);
        if (ret.first == -1) {
            return {};
        }

        segment_tree_.update_max(ret.first, k);
        return {ret.first, ret.second};
    }

    bool scatter(int k, int maxRow) {
        if (segment_tree_.query_sum(maxRow) >= k) {
            segment_tree_.update_sum(maxRow, k);
            return true;
        }
        return false;
    }

private:
    SegmentTree segment_tree_;
};
