    public BookMyShow(int n, int m) {
        bit=new long[n+1];
        int[] a=new int[n];
        st=new SegmentTree(a);
        this.m=m;
        for(int i=0;i<n;i++)
        {
            add(bit,i,m);
        }
    }

    public int[] gather(int k, int maxRow) {

        int f=st.helper(m-k);
        if(f==-1 || f>maxRow)
            return new int[0];
        int v=st.min(f);
        //update fenwick and segment trees accordingly
        add(bit,f,-k);
        st.update(f,v+k);
        return new int[]{f,v};
    }

    public boolean scatter(int k, int maxRow) {
        //fenwick tree used to sum the remaining capacity
        if(sum(bit,maxRow)<k)
            return false;

        while(k>0)
        {
            //Segment tree is used to get the first free spot
            int f=st.helper(m-1);
            if(f==-1 || f>maxRow)
                return false;
            int v=st.min(f);
            int val=Math.min(k,m-v);
            k-=val;
            st.update(f,v+val);
            add(bit,f,-val);
        }
        return true;
    }

    public long sum(long[] arr, int i)
    {
        long sum=0;
        for(++i;i>0;i-=(i&-i))
        {
            sum+=arr[i];
        }
        return sum;
    }
    public void add(long[] arr, int i, long v)
    {
        int n=arr.length;
        for(i++;i<n;i+=(i&-i))
        {
            arr[i]+=v;
        }
    }


    class SegmentTree
    {
        int H;
        int[] vals;
        public SegmentTree(int[] a)
        {
            int n=Integer.highestOneBit(Math.max(a.length-1,1))<<2;
            H=n>>1;
            vals=new int[n];
            Arrays.fill(vals,Integer.MAX_VALUE);

            for(int i=0;i<a.length;i++)
            {
                vals[i+H]=a[i];
            }

            for(int i=H-1;i>0;i--)
            {
                propogate(i);
            }
        }

        public void propogate(int i)
        {
            vals[i]=Math.min(vals[2*i], vals[(2*i)+1]);
        }

        public int min(int l)
        {
            l+=H;
            return vals[l];
        }
        public void update(int pos, int v)
        {
            vals[H+pos]=v;

            for(int i=(H+pos)>>1;i>=1;i>>=1)
            {
                propogate(i);
            }

        }
        public int helper(int l)
        {
            int cur=H;

            while(true)
            {
                if(vals[cur]<=l)
                {
                    if(cur>=H)
                    {
                        return cur-H;
                    }
                    cur=cur*2;
                }
                else {
                    cur++;
                    if((cur&(cur-1))==0)
                        return -1;
                    if((cur&1)==0)
                        cur>>=1;
                }
            }
        }
    }
}

