class BookMyShow {

    long[] lazy;
    long[] max;
    long[] sum;
    long n,m;
    int leaves;

    public BookMyShow(int n, int m) {
        this.n=n;
        this.m=m;
        leaves = (Integer.highestOneBit(n)==n) ? n : (Integer.highestOneBit(n)<<1); //next power of two
        int tnodes = leaves*2-1;
        lazy = new long[tnodes];
        Arrays.fill(lazy,-1);
        max = new long[tnodes];
        sum = new long[tnodes];
        update(0,0,leaves-1,0,n-1,m);
    }

    void set(int idx, int l, int r, long v){
        lazy[idx]=v;
        max[idx]=v;
        sum[idx]=v*(r-l+1);
    }

    void lazyProp(int idx, int l, int r){
        int mid = (l+r)/2;
        if(lazy[idx]!=-1){
            set(idx*2+1,l,mid,lazy[idx]);
            set(idx*2+2,mid+1,r,lazy[idx]);
            lazy[idx]=-1;
        }
    }

    void update(int idx, int l, int r, int ql, int qr, long v){
        if(ql<=l && qr>=r){
            set(idx,l,r,v);
            return ;
        }
        int mid = (l+r)/2;
        lazyProp(idx,l,r);
        if(ql<=mid){
            update(idx*2+1,l,mid,ql,qr,v);
        }
        if(qr>mid){
            update(idx*2+2,mid+1,r,ql,qr,v);
        }
        max[idx]=Math.max(max[idx*2+1],max[idx*2+2]);
        sum[idx]=sum[idx*2+1]+sum[idx*2+2];
    }

    int[] queryGather(int idx, int l, int r, int maxRow, int k){
        if(max[idx]<k){
            return new int[]{-1,-1};
        }
        if(l==r){
            return new int[]{l,idx};
        }
        lazyProp(idx,l,r);
        int mid = (l+r)/2;
        if(max[idx*2+1]>=k){
            return queryGather(idx*2+1,l,mid,maxRow, k);
        }
        if(maxRow>mid && max[idx*2+2]>=k){
            return queryGather(idx*2+2,mid+1,r,maxRow,k);
        }
        return new int[]{-1,-1};
    }

    int[] queryScatter(int idx, int l, int r, int maxRow, int k){
        if(sum[idx]<k){
            return new int[]{-1,-1,-1};
        }
        if(l==r){
            return new int[]{l,idx,k};
        }
        lazyProp(idx,l,r);
        int mid = (l+r)/2;
        if(sum[idx*2+1]>=k){
            return queryScatter(idx*2+1, l, mid, maxRow, k);
        }
        k-=sum[idx*2+1];
        if(maxRow>mid && sum[idx*2+2]>=k){
            return queryScatter(idx*2+2,mid+1,r,maxRow,k);
        }
        return new int[]{-1,-1,-1};
    }

    public int[] gather(int k, int maxRow) {
        int[] index = queryGather(0,0,leaves-1,maxRow,k);
        if(index[0]==-1) return new int[]{};
        int row = index[0];
        int col = (int) (m-sum[index[1]]);
        long rem = sum[index[1]]-k;
        update(0,0,leaves-1,row,row,rem);
        return new int[]{row,col};
    }

    public boolean scatter(int k, int maxRow) {
        int[] index = queryScatter(0,0,leaves-1,maxRow,k);
        if(index[0]==-1){
            return false;
        }
        int l = index[0];
        if(l>0){
            update(0,0,leaves-1,0,l-1,0);
        }
        long nvl = sum[index[1]]-index[2];
        update(0,0,leaves-1,l,l,nvl);
        return true;
    }
}
