class BookMyShow {
public:
    struct BIT {
        vector<long> arr;
        long N;

        BIT() {}

        BIT(int n) {
            N = long(n) + 1;
            arr = vector<long>(N, 0);
        }

        void add(long i, long val) {
            i += 1;
            while (i < N) {
                arr[i] += val;
                i += i & (-i);
            }
        }

        long query(long i) {
            long ret = 0;
            i += 1;
            while (i > 0) {
                ret += arr[i];
                i &= (i - 1);
            }
            return ret;
        }
    };

    struct SegTree {
        vector<int> arr;
        int n;

        void init(const vector<int>& vec, int v, int tl, int tr) {
            if (tl == tr) {
                arr[v] = vec[tl];
                return;
            }
            int mid = tl + (tr - tl) / 2;
            init(vec, 2 * v + 1, tl, mid);
            init(vec, 2 * v + 2, mid + 1, tr);
            arr[v] = max(arr[2 * v + 1], arr[2 * v + 2]);
        }

        SegTree() {}

        SegTree(const vector<int>& vec, const int& len) {
            n = len;
            arr = vector<int>(4 * n, 0);
            init(vec, 0, 0, n - 1);
        }

        void update(int v, int tl, int tr, const int& pos, const int& new_val) {
            if (tl == tr) {
                arr[v] = new_val;
                return;
            }
            int mid = tl + (tr - tl) / 2;
            if (pos <= mid) {
                update(2 * v + 1, tl, mid, pos, new_val);
                arr[v] = max(arr[2 * v + 1], arr[2 * v + 2]);
            }
            else {
                update(2 * v + 2, mid + 1, tr, pos, new_val);
                arr[v] = max(arr[2 * v + 1], arr[2 * v + 2]);
            }
        }

        int query(int v, int tl, int tr, int l, int r, const int& val) {
            if (l > r || arr[v] < val) return n;
            if (tl == tr) {
                if (arr[v] >= val) return tl;
                else return n;
            }
            int mid = tl + (tr - tl) / 2;
            int ret = query(2 * v + 1, tl, mid, l, min(r, mid), val);
            if (ret < n) return ret;
            ret = min(ret, query(2 * v + 2, mid + 1, tr, max(l, mid + 1), r, val));
            return ret;
        }
    };

    BookMyShow(int n, int m) : bit(n), st(vector<int>(n, m), n) {
        n_ = n;
        m_ = m;
        last_free = 0;
        capacity = vector<int>(n_, m_);
    }

    void decRow(int r, int k) {
        capacity[r] -= k;
        st.update(0, 0, n_ - 1, r, capacity[r]);
        bit.add(long(r), long(k));
    }

    vector<int> gather(int k, int maxRow) {
        int pr = st.query(0, 0, n_ - 1, 0, maxRow, k);
        if (pr == n_) return vector<int>();
        int pc = capacity[pr];
        decRow(pr, k);
        return vector<int>{pr, m_ - pc};
    }

    bool scatter(int k, int maxRow) {
        long avail = long(maxRow + 1) * long(m_) - bit.query(long(maxRow));
        if (long(k) > avail) return false;
        while (true) {
            if (capacity[last_free] == 0) last_free++;
            else {
                if (k > capacity[last_free]) {
                    k -= capacity[last_free];
                    decRow(last_free, capacity[last_free]);
                    last_free++;
                }
                else {
                    decRow(last_free, k);
                    break;
                }
            }
        }
        return true;
    }

private:
    vector<int> capacity;
    BIT bit;
    SegTree st;
    int n_, m_, last_free;
};

