            }
            return find_first_greater(seg_ri, seg_m, seg_r, k);
        }

        void update(int seg_i, int seg_l, int seg_r, int row, int val) {
            if (seg_r - seg_l == 1) {
                pre_max[seg_i] = val;
                pre_sum[seg_i] = val;
                row_cap[seg_l] = val;
                return;
            }
            int seg_m = seg_l + (seg_r - seg_l) / 2;
            int seg_li = 2 * seg_i + 1;
            int seg_ri = 2 * seg_i + 2;
            if (row < seg_m) {
