public int N;
public int M;
public Node[] tree;

public class QueryRes {
	public long cnt;
	public int idx;

	public QueryRes(long cnt, int idx) {
		this.cnt = cnt;
		this.idx = idx;
	}
}

public class Node {
	public int max;     // max remaining seats in one row
	public long cnt;    // total remaining seats in all rows
	public int m_idx;   // index for $max
	public int c_idx;   // starting index for $cnt

	public void calc(Node n1, Node n2) {
		max = Math.Max(n1.max, n2.max);
		cnt = n1.cnt + n2.cnt;
		m_idx = n1.max >= n2.max ? n1.m_idx : n2.m_idx;
		c_idx = Math.Min(n1.c_idx, n2.c_idx);            
	}

	public int book(int bcnt) {
		int r = Math.Max(0, bcnt - (int)cnt);
		cnt = Math.Max(0, (int)cnt - bcnt);
		max = (int)cnt;
		if (cnt == 0) {
			m_idx = int.MaxValue;
			c_idx = int.MaxValue;
		}
		return r;
	}
}

int updateTreeNode(int idx, int needCnt) {
	int res = tree[idx + N].book(needCnt);
	// move upward and update parents
	for (int i = idx + N; i > 1; i >>= 1) {
		tree[i >> 1].calc(tree[i], tree[i^1]);
	}
	return res;
}

void buildTree() {
	for (int i = 0; i < N; i++) {
		tree[N + i] = new Node();
		tree[N + i].max = M; tree[N + i].cnt = M;
		tree[N + i].m_idx = i; tree[N + i].c_idx = i;
	}
	// build the tree by calculating parents
	for (int i = N - 1; i > 0; --i) {
		tree[i] = new Node();
		tree[i].calc(tree[i << 1], tree[i << 1 | 1]);
	}
}

QueryRes query(int l, int r, Action<Node, QueryRes> f) {
	QueryRes res = new QueryRes(0, int.MaxValue);
	for (l += N, r += N; l < r; l >>= 1, r >>= 1) {
		if ((l&1) > 0) { f(tree[l++], res); }
		if ((r&1) > 0) { f(tree[--r], res); }
	}
	return res;
}

public BookMyShow(int n, int m) {
	N = 1; M = m;
	while(N < n) { N *= 2; }
	tree = new Node[2 * N];
	buildTree();
}

public int[] Gather(int k, int maxRow) {        
	QueryRes res = binarySearch(k, 0, maxRow, MaxFunc);
	if ((int)res.cnt < k) return (new List<int>()).ToArray();

	int r1 = res.idx;
	int r2 = M - (int)res.cnt;
	updateTreeNode(res.idx, k);
	return new int[2] {r1, r2};
}

public bool Scatter(int k, int maxRow) {
	QueryRes res = binarySearch(k, 0, maxRow, SumFunc);
	if ((int)res.cnt < k) return false;

	while(k > 0) { k = updateTreeNode(res.idx++, k); }
	return true;
}

// query range [l, r)
public QueryRes binarySearch(int k, int l, int r, Action<Node, QueryRes> f) {
	QueryRes res;
	while(l<r) {
		int m = (l + r)/2;
		res = query(0, m+1, f);
		if (res.cnt >= k) { r = m; }
		else { l = m+1; }
	}
	res = query(0, r+1, f);
	return res;
}

public void MaxFunc(Node node, QueryRes res) {
	if (node.max > res.cnt) { res.idx = node.m_idx; } 
	else if(node.max == res.cnt && node.m_idx < res.idx) { res.idx = node.m_idx; }
	res.cnt = Math.Max(res.cnt, node.max);
}

public void SumFunc(Node node, QueryRes res) {
	res.cnt += node.cnt;
	res.idx = Math.Min(res.idx, node.c_idx);
}
