class BookMyShow {
public:
    long long int BIT[100000] = {};
    long long int row[100000] = {};
    int lo = 0;
    long long int M;
    long long int N;
    BookMyShow(int n, int m) {
        M = m;
        N = n;

        for(int i=0;i<n;i++) update(i, M);
    }

    long long int tot(int n){
        n++;
        long long int sum = 0;
        while(n > 0){
            sum += BIT[n];
            n -= n&(-n);
        }
        return sum;
    }

    void update(int n, int val){
        n++;
        while(n <= N){
            BIT[n] += val;
            n += n&(-n);
        }
    }

    vector<int> gather(int k, int maxRow) {
        if(lo > maxRow) return {};
        long long int sum = tot(maxRow);
        if(sum < k) return {};
        int ind = lo;
        while(ind <= maxRow){
            long long int seat = M - row[ind];
            if(seat >= k){
                update(ind, -1*k);
                row[ind] += k;
                return {ind, (int)(M - seat)};
            }
            ind++;
        }
        return {};

    }

    bool scatter(int k, int maxRow) {

        if(lo > maxRow) return false; 
        long long int seat = tot(maxRow);
        if(seat < k) return false;
        while(k){
            long long int seat = M - row[lo];
