			if(meth!=MAJ) tree[indx]=merge(tree[indx*2+1], tree[indx*2+2]); 
			else{ cnt[indx]= (tree[indx*2+1]!=tree[indx*2+2])? abs(cnt[indx*2+1]- cnt[indx*2+2]): cnt[indx*2+1]+cnt[indx*2+2];
				tree[indx]=merge(tree[indx*2+1], tree[indx*2+2], cnt[indx*2+1], cnt[indx*2+2] ); 
			}
		}
