using namespace myleetcode;

class BookMyShow {
public:

    mp_ii row;
    int  m,n;
      struct SEGT {
          vl tree; vi cnt; int len; merge_method meth; vi ori; SEGT(){;}
	SEGT(int n, merge_method m, int ini){ tree=vl(4*(n+1), ini); ori=vi(n,ini); meth=m; len=n;}
	SEGT(merge_method m, vi& v){ int n=v.size(); tree=vl(4*(n+1), 0);  meth=m; len=n; ori=vi(v); 
                                 if(meth==MAJ) {cnt=vi(4*(n+1), 1);}
	  construct(0, n-1,0);
	}
        void construct(int left, int right, int indx){
		if(left==right) { tree[indx]=ori[left]; if(meth==MAJ) cnt[left]=1;}
		else{ int mid=(right-left)/2+left;  construct(left, mid, indx*2+1); construct(mid+1, right,indx*2+2); 
			if(meth!=MAJ) tree[indx]=merge(tree[indx*2+1], tree[indx*2+2]); 
			else{ cnt[indx]= (tree[indx*2+1]!=tree[indx*2+2])? abs(cnt[indx*2+1]- cnt[indx*2+2]): cnt[indx*2+1]+cnt[indx*2+2];
				tree[indx]=merge(tree[indx*2+1], tree[indx*2+2], cnt[indx*2+1], cnt[indx*2+2] ); 
			}
		}
	}
	void update(int target, ll val){ ori[target]=(int)val; update(target, 0, len-1, 0);}
    ll   query(int front, int end){ if(meth!=MAJ) return query(front, end, 0,len-1,0); 
                                     else         return query_maj(front, end,0,len-1,0).fi;}
    ll merge(ll a, ll b, int a_more=0, int b_more=0 ){
        if(meth==XOR) { if(a==INT_MIN) return b; else if(b==INT_MIN) return a; else return a^b; }
        else if(meth==OR) { if(a==INT_MIN) return b; else if(b==INT_MIN) return a;  else return a|b;}
        else if(meth==AND) {  if(a==INT_MIN) return b; else if(b==INT_MIN) return a; else return a&b;}
        else if(meth==SUM) {  if(a==INT_MIN) return b; else if(b==INT_MIN) return a; else return a+b;}
        else if(meth==MAX) {  if(a==INT_MIN) return b; else if(b==INT_MIN) return a; else return max(a,b);}
        else if(meth==MIN)  {  if(a==INT_MIN) return b; else if(b==INT_MIN) return a; else return min(a,b); }
       else if(meth==MAJ)  {  if(a==INT_MIN) return b; else if(b==INT_MIN) return a; else { return (a_more>=b_more)? a:b; } }
        return 0;
    }
    void update(int target, int left, int right, int indx){
            if(target<left||target>right) return ;
            if(left==right) {if(left==target) tree[indx]=ori[target]; return;}
             int mid= (right-left)/2+left; 
             if(target<=mid) update(target,left,mid,indx*2+1); 
             else            update(target,mid+1,right,indx*2+2);
             if(meth!=MAJ) tree[indx]= merge(tree[indx*2+1], tree[indx*2+2]);
            else { cnt[indx]= (tree[indx*2+1]!=tree[indx*2+2])?abs(cnt[indx*2+1]- cnt[indx*2+2]): cnt[indx*2+1]+cnt[indx*2+2];
                     tree[indx]= merge(tree[indx*2+1], tree[indx*2+2], cnt[indx*2+1], cnt[indx*2+2]);
		}     
      }
   pii query_maj(int front, int end, int left, int right, int indx){
	if(end<left|| front>right) return {INT_MIN,0};
        if(left==right){ if(left>=front&& end>=left) return {ori[left],1}; else return {INT_MIN,0};}
        else if( front<=left&& right<=end) return {(int)tree[indx], (int)cnt[indx]};
        else{  int mid= (right-left)/2+left; 
             pii f1=   query_maj(front, end,left,mid,indx*2+1);  pii f2= query_maj(front,end,mid+1,right,indx*2+2);
	    if(f1.fi==f2.fi) return {f1.fi, f1.se+f2.se};
            else{ return {  (f1.se>f2.se)? f1.fi: f2.fi, abs(f1.se-f2.se)}; }
        }
    }
    ll query(int front, int end, int left,int right,int indx){
        if(end<left|| front>right) return INT_MIN;
        if(left==right){ if(left>=front&& end>=left) return tree[indx]; else return INT_MIN;}
        else if( front<=left&& right<=end) return tree[indx];
        else{  int mid= (right-left)/2+left; 
               ll f1=   query(front, end,left,mid,indx*2+1);  ll f2= query(front,end,mid+1,right,indx*2+2);
             return merge(f1,f2);
        }
    }
 }; // binary index tree 
    SEGT maxSeg, sumSeg;
    bool isLE(int r, int target){  return maxSeg.query(0, r)>=target;}
    BookMyShow(int nn, int mm) { m=mm; n=nn; vi tmp(n,m);
                                maxSeg=SEGT(MAX, tmp); sumSeg=SEGT(SUM, tmp);
                                for(int i=0;i<n;i++) { row[i]=m; }
                               }

    // (false , false,true)
    vector<int> gather(int k, int maxRow) {
        int left=0; int right= maxRow;
        int target=left;
        while(right>=left){ int mid=(right-left)/2+left;
            if(right==left){ if(isLE(left,k)){target=left;break;} else {return {}; }}
            else if(right-left==1){ if(isLE(left,k)){target=left;break;} 
                                    if(isLE(right,k)){target=right;break;}
                               if(right==maxRow){ return {}; } else {target=right+1; break;}
                             }
            else{ if(isLE(mid,k)) right=mid;  else left=mid;}
        }
        row[target]-=k; int tmp=row[target]; 
        maxSeg.update(target, tmp); sumSeg.update(target,tmp);
        vi ans={target,m-tmp-k};
        if(tmp==0) row.era(target);

        return ans;
    }

    bool scatter(int k, int maxRow) { int tmp=0; vi cand;
        if((ll)k>sumSeg.query(0,maxRow)) return false;
        for(auto&x:row){
            if(x.fi>maxRow) break;
            tmp+=x.se; cand.pb(x.fi); sumSeg.update(x.fi,0); maxSeg.update(x.fi,0); if(tmp>=k) break;
        }
        for(int i=0;i<sz(cand)-1;i++) {row.era(cand[i]); }
        row[cand.back()]=tmp-k;     
        if(row[cand.back()]==0) row.era(cand.back());
        else { sumSeg.update(cand.back(),tmp-k); maxSeg.update(cand.back(), tmp-k); }
        //cout<<row<<endl;
        return true;                             
    }
};
