class BookMyShow {
    struct SegmentInfo {
        int row_free_place;
        int64_t whole_free_place;
    };

    int n_;
    int m_;
    std::vector<SegmentInfo> segmentation_index_;

    int size_;
    int first_row_index_;

    int allocate(int row, int places)
    {
        int segment_index = row + first_row_index_;
        auto& row_segment = segmentation_index_[segment_index];
        const int prev_places = row_segment.row_free_place;

        row_segment.row_free_place -= places;
        row_segment.whole_free_place -= places;
        assert(row_segment.row_free_place >= 0);

        while (segment_index > 0)
        {
            segment_index = (segment_index - 1) / 2;
            auto& segment = segmentation_index_[segment_index];
            segment.whole_free_place -= places;
            segment.row_free_place = std::max(segmentation_index_[segment_index * 2 + 1].row_free_place, segmentation_index_[segment_index * 2 + 2].row_free_place);
        }

        return m_ - prev_places;
    }

    void build(int beg, int binary_end, int end, int segment)
    {
        auto& info = segmentation_index_[segment];
        info.row_free_place = m_;

        if (beg == binary_end)
        { 
            info.whole_free_place = m_;
        }
        else
        {
            info.whole_free_place = int64_t(end - beg + 1)* m_;
            int middle = beg + (binary_end - beg + 1) / 2;

            if (end >= middle)
            {
                build(beg, middle - 1, middle - 1, segment * 2 + 1);
                build(middle, binary_end, end, segment * 2 + 2);
            }
            else
            {
                build(beg, middle - 1, end, segment * 2 + 1);
            }
        }
    }

    int64_t get_common_free(int beg, int binary_end, int end, int seg) const
    {
        if (binary_end == end)
        {
            return segmentation_index_[seg].whole_free_place;
        }

        const int middle = beg + (binary_end - beg + 1) / 2;

        if (end < middle)
        {
            return get_common_free(beg, middle - 1, end, seg * 2 + 1);
        }

        return segmentation_index_[seg * 2 + 1].whole_free_place + get_common_free(middle, binary_end, end, seg * 2 + 2);
    }

    int get_row_free(int beg, int binary_end, int end, int seg, int places)
    {
        const auto& info = segmentation_index_[seg];

        if (info.row_free_place < places)
        {
            return -1;
        }

        if (beg == binary_end)
        {
            return info.row_free_place >= places ? beg : -1;
        }

        const int middle = beg + (binary_end - beg + 1) / 2;
        const int first_bottom = get_row_free(beg, middle - 1, end, seg * 2 + 1, places);

        if (first_bottom != -1 || end < middle)
        {
            return first_bottom;
        }

        return get_row_free(middle, binary_end, end, seg * 2 + 2, places);
    }

public:
    BookMyShow(int n, int m) : n_(n), m_(m) {
        size_ = 1;

        while (size_ < n)
            size_ *= 2;

        segmentation_index_.resize(size_ * 2 - 1);
        memset(segmentation_index_.data(), 0, segmentation_index_.size() * sizeof(SegmentInfo));
        first_row_index_ = size_ - 1;

        build(0, size_ - 1, n - 1, 0);
    }

    vector<int> gather(int k, int maxRow) {
        const int first_free = get_row_free(0, size_ - 1, maxRow, 0, k);
        if (first_free != -1)
        {
            return std::vector<int>{first_free, allocate(first_free, k)};
        }

        return std::vector<int>{};
    }

    bool scatter(int k, int maxRow) {
        if (get_common_free(0, size_ - 1, maxRow, 0) >= k)
        {
            int free_row = get_row_free(0, size_ - 1, maxRow, 0, 1);
            assert(free_row != -1);

            while (k > 0)
            {
                int free_places = std::min(segmentation_index_[free_row + first_row_index_].row_free_place, k);
                allocate(free_row, free_places);
                k -= free_places;
                ++free_row;
            }

            return true;
        }

        return false;
    }
};
