vector<long long > sum ;

    int N , M ;
    int root , sz ;

    BookMyShow(int n, int m) {
        N = n ;
        M = m ;
        root = sqrt(N) ;
        sz = (N+root-1)/root ;

        int rem = N  ;
        avail.resize(sz+5) ;
        mx.resize(sz+5) ;
        sum.resize(sz+5) ;

        for(int i = 0 ; i<sz; i++ ){
            int k = root ;
            while(k>0 and rem>0 ){
                avail[i].push_back(M) ;
                mx[i] = M ;
                sum[i] += M ;
                k-- ;
                rem-- ;
            }
        }
    }

    vector<int> gather(int k, int maxRow) {
        if(k>M) return {} ;
        int  id = 0 ;

        for(int i = 0 ; id<=maxRow ; i++ ){

            if(mx[i]>=k){

                for(int j = 0 ; j<avail[i].size() ;j++ ){

                    if(avail[i][j]>=k){

                        if(id>maxRow) return {} ;

                        int kk = M - avail[i][j] ;
                        vector<int> ans = {id , kk } ;

                        avail[i][j]-=k ;

                        // update i ;
                        long long  mxs = 0 , sm = 0 ;
                        for(int k = 0 ; k<avail[i].size() ; k++ ){
                            sm+=avail[i][k];
                            mxs = max(mxs , avail[i][k] ) ;
                        }
                        sum[i] = sm ;
                        mx[i] = mxs ;

                        return ans ;
                    }
                    id++;
                }
            }
            id+=root ;
        }

        return {} ;
    }

    bool scatter(int kk, int maxRow) {
        long long k = kk ;
        long long totsum = 0 ;
