            }
            id+=root ;
        }

        if(k > totsum ) return false ;

        for(int i = 0 ; i<sum.size() ; i++ ){

            if(sum[i]<k ){
                k-=sum[i] ;
                sum[i] = 0 ;
                mx[i] = 0 ;
                for(int j=0;j<avail[i].size();j++){
                    avail[i][j] =0;
                }
            }
            else{
                for(int j = 0 ; j<avail[i].size(); j++ ){
                    long long  here = min(k , avail[i][j] ) ;
                    avail[i][j]-= here ;
                    k -= here ;
