#define N 50176
#define M 224
#define headIndex(x) (x/M)
typedef long long ll;
class BookMyShow {
    int n, m, start = 0;
    vector<ll> headMax, headSum;
    vector<int> detail;
    long long totalAvailable;
public:
    BookMyShow(int n, int m): n(n), m(m) {
        headMax.resize(M, m);
        headSum.resize(M, (ll)m*M);
        detail.resize(N, m);
        totalAvailable = (ll)n*m;
    }

    tuple<int, int, ll> search(int k, int maxRow, vector<ll> &source, function<ll(ll, ll)> combine) {
        int i = headIndex(start);
        ll initial = 0, temp;
        while(i < headIndex(n) && (temp = combine(initial, source[i])) < k) {
            initial = temp;
            i++;
        }
        for(int j = M*i, endIndex = min(min(M*(i+1), n), maxRow+1); j < endIndex; j++)
            if((initial = combine(initial, detail[j])) >= k)
                return {j+1, i, initial};
        return {-1, 0, 0};
    }

    vector<int> gather(int k, int maxRow) {
        auto [j, i, ignore] = search(k, maxRow, headMax, [](auto a, auto b) { return max(a, b); });
        if(j == -1) return {};
        detail[j-1] -= k;
        headSum[i] -= k;
        totalAvailable -= k;
        int startIndex = M*i, endIndex = min(M*(i+1), n);
        headMax[i] = *max_element(detail.begin()+startIndex, detail.begin()+endIndex);
        return {j-1, m-detail[j-1]-k};
    }

    bool scatter(int k, int maxRow) {
        if(totalAvailable < k) return false;
        auto [found, i, available] = search(k, maxRow, headSum, [](auto a, auto b) { return a+b; });
        if(found == -1) return false;
        for(int j = headIndex(start); j <= i; j++) headMax[j] = headSum[j] = 0;
        fill(detail.begin()+start, detail.begin()+found, 0);
        detail[found-1] = available-k;
        int startIndex = M*i, endIndex = min(M*(i+1), n);
        headMax[i] = *max_element(detail.begin()+startIndex, detail.begin()+endIndex);
        headSum[i] = accumulate(detail.begin()+startIndex, detail.begin()+endIndex, 0ll);
        start = found - (detail[found-1] != 0);
        totalAvailable -= k;
        return true;
    }
};
