solutions.content:
If you are interested, please ask questions and I'll answer them. If requested, will add comments to the code and more explanation of the solution.

`seats` is square root decomposed list of number of available seats in each row;
`sums[i]` denotes sum of total available seats in bucket `i`-th;
`maxs[i]` denotes maximum number of available seats in any row in `i`-th bucket;
```
#define ll long long
class BookMyShow {
    ll n, m;
    ll n_left;
    ll bucket_size;
    vector<vector<ll>> seats;
    vector<long long> maxs;
    vector<long long> sums;

public:

    BookMyShow(int _n, int _m) {
        n = _n;
        m = _m;
        bucket_size = sqrt(n) + 1;
        n_left = n * m;
        seats = vector<vector<ll>>(bucket_size, vector<ll>(bucket_size, m));
        maxs = vector<ll>(bucket_size, m);
        sums = vector<ll>(bucket_size, m*bucket_size);
    }

    vector<int> gather(int k, int maxRow) {
        // print("gather k=" + to_string(k) + " maxRow=" + to_string(maxRow));
        if (n_left < k) {
            return {};
        }
        for (int i = 0; i < bucket_size; i++) {
            if (i * bucket_size > maxRow) {
                return {};
            }
            if (maxs[i] < k) {
                continue;
            }
            for (int j = 0; j < bucket_size; j++) {
                const int row = i * bucket_size + j;
                if (row > maxRow) {
                    return {};
                }
                if (seats[i][j] < k) {
                    continue;
                }
                const int col = m - seats[i][j];
                seats[i][j] -= k;
                maxs[i] = 0;
                sums[i] = 0;
                for (const ll& s : seats[i]) {
                    maxs[i] = max(maxs[i], s);
                    sums[i] += s;
                }
                n_left -= k;
                return {row, col};
            }
        }
        return {};
    }

    bool scatter(int k, int maxRow) {
        // print("scatter k=" + to_string(k) + " maxRow=" + to_string(maxRow));
        if (n_left < k) {
            return false;
        }
        ll n_available = 0;
        for (int i = 0; i < bucket_size; i++) {
            if (i * bucket_size > maxRow) {
                goto Outer;
            }
            if (n_available + sums[i] >= k) {
                for (int j = 0; j < bucket_size; j++) {
                    const int row = i * bucket_size + j;
                    if (row > maxRow) {
                        goto Outer;
                    }
                    n_available += seats[i][j];
                }
                goto Outer;
            } else {
                n_available += sums[i];
            }
        }
        Outer:
        if (n_available < k) {
            return false;
        }
        n_left -= k;
        for (int i = 0; i < bucket_size and k > 0; i++) {
            if (sums[i] >= k) {
                for (int j = 0; j < bucket_size; j++) {
                    const int row = i * bucket_size + j;
                    ll able = min(seats[i][j], (ll)k);
                    seats[i][j] -= able;
                    k -= able;
                    if (k == 0) {
                        sums[i] = 0;
                        maxs[i] = 0;
                        for (const ll& s : seats[i]) {
                            maxs[i] = max(maxs[i], s);
                            sums[i] += (ll)s;
                        } 
                        return true;
                    }
                }
            } else {
                k -= sums[i];
                sums[i] = 0;
                maxs[i] = 0;
            }
        }
        return true;
    }

private:
    void print(string prelude="") {
        cout << "----------------------------------------------------------------------------------" << endl;
        if (not prelude.empty()) {
            cout << prelude << endl;
        }
        cout << "n=" << n << " m=" << m << " bucket_size=" << bucket_size << " n_left=" << n_left << endl;
        cout << "maxs=[ ";
        for (const ll& m : maxs) {
            cout << m << " ";
        }
        cout << "]" << endl;
        cout << "sums=[ ";
        for (const ll& s : sums) {
            cout << s << " ";
        }
        cout << "]" << endl;
        cout << "seats=[" << endl;
        for (const auto& bucket : seats) {
            cout << "\t[ ";
            for (const ll& b : bucket) {
                cout << b << " ";
            }
            cout << "]" << endl;
        }
        cout << endl << "]" << endl;
        cout << "----------------------------------------------------------------------------------" << endl;
    }

};
#undef ll
