function Fenwick(n) {
    let a = Array(n).fill(0);
    return { query, update, rangeSum, tree }
    function query(i) { // [0, i] prefix sum
        let sum = 0;
        for (i++; i > 0; i = parent(i)) sum += a[i];
        return sum;
    }
    function update(i, v) {
        for (i++; i < n; i = next(i)) a[i] += v;
    }
    function rangeSum(l, r) {
        return query(r) - query(l - 1);
    }
    function parent(x) {
        return x - lowestOneBit(x);
    }
    function next(x) {
        return x + lowestOneBit(x);
    }
    function lowestOneBit(x) {
        return x & -x;
    }
    function tree() {
        return a;
    }
}

function BookMyShow(n, m) {
    let used = Array(n).fill(0), fen = new Fenwick(n + 3);
    for (let i = 0; i < n; i++) fen.update(i, m); // original set each node with m seats
    return { gather, scatter }
    function gather(k, maxRow) {
        for (let i = 0; i <= maxRow; i++) {
            let seat = m - used[i];
            if (seat >= k) {
                let first = used[i];
                used[i] += k;
                fen.update(i, -k); // remove k seats in tree node
                return [i, first];
            }
        }
        return [];
    }
    function scatter(k, maxRow) {
        let totToMaxRow = fen.query(maxRow); // use fenwick to query [0, maxRow] range sum
        if (totToMaxRow < k) return false;
        for (let i = 0; i <= maxRow; i++) {
            let seat = m - used[i];
            if (seat >= k) {
                used[i] += k;
                fen.update(i, -k);
                k = 0;
            } else {
                k -= seat;
                fen.update(i, -seat);
                used[i] = m;
            }
        }
        return true;
    }
}
