class BookMyShow {
    int n, m;
    Seg seg;
    Queue<Integer> pq = new LinkedList<>();
    public BookMyShow(int n, int m) {
        this.n = n;
        this.m = m;
        seg = new Seg(0, n - 1);
        for(int i = 0; i < n; i++) {
            pq.add(i);
        }
    }

    public int[] gather(int k, int maxRow) {
        int x = m - k;
        int mn = seg.query(0, maxRow);
        if(mn > x) {
            return new int[]{};
        }

        int index = dfs(seg, maxRow, x);
        int old =  seg.query(index, index);
        seg.update(index, old + k);
        return new int[]{index, old};
    }

    public boolean scatter(int k, int maxRow) {
        if(pq.size() == 0) return false;

        int index = pq.peek();
        if(index > maxRow) {
            return false;
        }

        long sum = seg.query1(0, maxRow);
        if((m + 0l) * (maxRow + 1) - sum < k) return false;

        while(k > 0) {
            int peek = pq.peek();
            int old = (int)(seg.query1(peek, peek));
            int remain = m - old;
            if(remain > k) {
                seg.update(peek, old + k);
                k = 0;
            } else {
                k -= remain;
                pq.poll();
                seg.update(peek, m);
            }
        }
        return true;
    }

    public int dfs(Seg root, int bound, int x) {
        int l = root.l, r = root.r;
        if(l == r) {
            return l;
        }
        int mid = l + (r - l) / 2;


        if(bound <= mid) {
            return dfs(root.left, bound, x);
        } else {
            if(root.left.min <= x) {
                return dfs(root.left, bound, x);
            } else {
                return dfs(root.right, bound, x);
            }
        }
    }

    class Seg{
        int l,r;
        int min = 0;
        long sum = 0;
        Seg left=null,right=null;
        public Seg(int l,int r){
            this.l=l;
            this.r=r;
            if(l!=r){
                int mid=l+(r-l)/2;
                if(l<=mid)left=new Seg(l,mid);
                if(r>=mid+1)right=new Seg(mid+1,r);
            }else{

            }
        }
        public int query(int s,int e){//query min
            if(e<0||s<0)return 0;
            if(l==s&&r==e){
                return min;
            }
            int mid=l+(r-l)/2; //left :  to mid-1,
            if(e<=mid){
                return left.query(s,e);
            }
            else if(s>=mid+1){
                return right.query(s,e);
            }else{
                return Math.min(left.query(s,mid),right.query(mid+1,e));
            }
        }

        public long query1(int s,int e){ //query sum
            if(l==s&&r==e){
                return this.sum;
            }

            int mid=l+(r-l)/2; //left :  to mid-1,
            if(e<=mid){
                return left.query1(s,e);
            }
            else if(s>=mid+1){
                return right.query1(s,e);
            }else{
                return left.query1(s,mid) + right.query1(mid+1,e);
            }
        }

        public void update(int index,int val){
            if(l==r){
                this.min=val;
                this.sum = val;
                return;
            }
            int mid=l+(r-l)/2;
            if(index<=mid){
                left.update(index,val);
            }else{
                right.update(index,val);
            }

            this.min=Math.min(left.min,right.min);
            this.sum = left.sum + right.sum;
        }

    }
}

