function BookMyShow(n, m) {
    let used = Array(n).fill(0);
    return { gather, scatter }
    function gather(k, maxRow) {
        for (let i = 0; i <= maxRow; i++) {
            let seat = m - used[i];
            if (seat >= k) { // for gathering only current row has more than or equal to k seat, can operate
                let first = used[i];
                used[i] += k;
                return [i, first];
            }
        }
        return [];
    }
    function scatter(k, maxRow) { // arrange k to [0, maxRow], if possible, update used array
        let totToMaxRow = 0;
        for (let i = 0; i <= maxRow; i++) totToMaxRow += m - used[i];
        if (totToMaxRow < k) return false;
        for (let i = 0; i <= maxRow; i++) {
            let seat = m - used[i];
            if (seat >= k) {
                used[i] += k;
                k = 0;
            } else {
                k -= seat;
                used[i] = m;
            }
        }
        return true;
    }
}
