#include <bits/stdc++.h>
using namespace std;

class BookMyShow {
 public:
  long long BIT[50001] = {};
  int rowCnt[50001] = {}, n = 0, m = 0, start = 0;
  BookMyShow(int n, int m) {
    this->n = n;
    this->m = m;
  }

  long long BIT_PrefixSum(int i) {
    long long sum = 0;
    for (i = i + 1; i > 0; i -= i & (-i)) sum += BIT[i];
    return sum;
  }

  void add(int i, int val) {
    for (i = i + 1; i <= 50000; i += i & (-i)) BIT[i] += val;
  }
  vector<int> gather(int k, int maxRow) {
    // find total occupied place till maxRow
    long long total = BIT_PrefixSum(maxRow);

    if (total + k > ((long long)maxRow + 1) * m) return {};

    for (int i = start; i <= maxRow; ++i) {
      if (rowCnt[i] + k > m) continue;
      rowCnt[i] += k;
      add(i, k);
      return {i, rowCnt[i] - k};
    }
    return {};
  }
  bool scatter(int k, int maxRow) {
    long long total = BIT_PrefixSum(maxRow);

    if (total + k > ((long long)maxRow + 1) * m) return false;

    for (int i = start; k && i <= maxRow; ++i) {
      if (m - rowCnt[i]) {
        // take partial
        int seatOccupied = min(m - rowCnt[i], k);

        // remaining seats
        k -= seatOccupied;

        // add Occupied Seat to BIT till index i
        add(i, seatOccupied);

        // Store occupied seat to rowCnt
        rowCnt[i] += seatOccupied;
      } else {
        // take full
        start = i + 1;
      }
    }
    return true;
  }
};
