class BookMyShow {
    int rows,columns;
    vector<long long>cnt_occupied; // no of seats occupied in each row;
    int min_avaliable_row = 0; // stores the minimum index of row which contains at least one empty seat. (Used just to make the search space smaller)

public:

    BookMyShow(int n, int m) {
        rows=n, columns = m;
        cnt_occupied = vector<long long>(n); 
    }

    vector<int> gather(int k, int maxRow) {
	// finds the row with smallest index, that has >=k seats

        vector<int>ans;
        for(int i=min_avaliable_row;i<=maxRow;i++){
            if(columns-cnt_occupied[i]>=k){
                ans.push_back(i);
                ans.push_back(cnt_occupied[i]);
                cnt_occupied[i]+=k;
                return ans;
            }

        }
        return ans;
    }

    bool scatter(int k, int maxRow) {
        //greedily assigns seats
        int prevIndex= -1, i=min_avaliable_row;
        int left = k;
		int min_start = min_avaliable_row;
        while(i<=maxRow&& left>0){
            int row_left = columns - cnt_occupied[i]; // number  of unoccupied seats in row[i]
            if(row_left>=left){
				//It's possible that we can get K seats.
                cnt_occupied[i]+=left;
                prevIndex = i-1;
                min_avaliable_row = i;
                left = 0;
            }else if(row_left>0){
                left-=row_left;
            }
            i++;
        }

        if(left==0){
           while(prevIndex>=min_start){
		   // set all prev row's seats to occupied
                cnt_occupied[prevIndex] =columns;
                prevIndex--;
            }
            return true;
            return true;
        }
        return false;
    }
};
