#define ll long long
class sg{

    public:
    vector<ll> seg;
    int N;
    void merge(ll & cur,ll & left,ll & right){
              cur=max(left,right);
    }
    ll Query(int qs,int qe,int si,int ss,int se){ 
         if(ss>qe||se<qs||ss>se)
              return -1 ;
             if(ss>=qs&&se<=qe)
              return seg[si];
             int mid=(se+ss)/2;
             ll le=Query(qs,qe,2*si+1,ss,mid);
             ll ri=Query(qs,qe,2*si+2,mid+1,se);
             ll res;
             merge(res,le,ri);
             return res;
    }
    void Update(int qs,int qe,int diff,int si,int ss,int se)
    {
                if(ss>qe||se<qs||ss>se)
                    return ;
                if(ss>=qs&&se<=qe)
                 {
                   seg[si]=diff;
                   return ;
                 }

            int mid=(se+ss)/2;

            Update(qs,qe,diff,2*si+1,ss,mid);
            Update(qs,qe,diff,2*si+2,mid+1,se);

            merge(seg[si],seg[2*si+1],seg[2*si+2]);
    }
    void Build(ll val,int si,int ss,int se)
    {        if(ss>se) return;
             if(ss==se){
             seg[si]=val;return ;}
             int mid=(ss+se)/2;
             Build(val,2*si+1,ss,mid);
             Build(val,2*si+2,mid+1,se);
             merge(seg[si],seg[2*si+1],seg[2*si+2]);
    }
    void build(ll val){ Build(val,0,0,N-1);}
    ll query(ll qs,ll qe){ return Query(qs,qe,0,0,N-1);}
    void update(ll qs,ll qe,ll val){ Update(qs,qe,val,0,0,N-1);}
};

