class BookMyShow:

    def __init__(self, n: int, m: int):

        self.allocations = defaultdict(list)

        for r in range(n):
            for c in range(m):
                heappush(self.allocations[r], c)

    def gather(self, k: int, maxRow: int) -> List[int]:
        # Find row with enough seats
        startRow = 0
        while len(self.allocations[startRow]) < k:
            startRow += 1

        # Exceed max row
        if startRow > maxRow:
            return []

        # Not continuous
        startSeat = self.allocations[startRow][0]
        endSeat = self.allocations[startRow][k - 1]
        if endSeat - startSeat != k - 1:
            return []

        # Allocate the seats
        for i in range(k):
            heappop(self.allocations[startRow])

        return [startRow, startSeat]

    def scatter(self, k: int, maxRow: int) -> bool:
        startRow = 0

        while len(self.allocations[startRow]) < k:
            startRow += 1

        if startRow > maxRow:
            return False

        # Allocate the seats
        for i in range(k):
            heappop(self.allocations[startRow])

        return True
