        counter = 0
        start = None
        for r in range(maxRow + 1):
            for c in range(len(self.seats[0])):

                # If the seat is empty, try to fill the row with people sitting
                # side by side
                if self.seats[r][c] == 0:
                    counter += 1

                    if start is None:
                        start = c

                    if counter == k:
                        for chair in range(start + k):
                            self.seats[r][chair] = 1

                        return [r, start]
                else:
                    continue

            # We need to reset where the first chair is because the row
            # Did not fit everyone
            counter = 0
            start = None

        return []

