        temp = copy.deepcopy(self.seats)

        for r in range(maxRow + 1):
            for c in range(len(self.seats[0])):

                # If the seat is empty, fill it
                if temp[r][c] == 0:
                    counter += 1
                    temp[r][c] = 1

                    if counter == k:
                        self.seats = temp
                        return True

        return False
