type BookMyShow struct {
    seats []int
    row int
    col int // maximum number of seats that a row has
}


func Constructor(n int, m int) BookMyShow {
    return BookMyShow{
        seats : make([]int,n),
        row : n,
        col : m,
    }
}


func (this *BookMyShow) Gather(k int, maxRow int) []int {
    for i:=0;i<=maxRow;i++{
        if this.seats[i] + k <= this.col{
            ans := []int{i,this.seats[i]}
            this.seats[i] = this.seats[i] + k
            return ans
        }
    }

    return []int{}
}


func (this *BookMyShow) Scatter(k int, maxRow int) bool {
    availableSeats := 0
    for i:=0;i<=maxRow;i++{
        availableSeats += this.col - this.seats[i]
    }

    // do not fill the seats if it is insufficient
    if availableSeats < k{
        return false
    }

    // if the seats sufficient
    for i:=0;i<=maxRow;i++{
        // fill the smallest row first
        currFillRow := min(this.col-this.seats[i],k)
        this.seats[i] += currFillRow
        k -= currFillRow
        if k == 0{
            break
        }
    }

    return true
}

